import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie, Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

const AnalyticsPage = () => {
  const [analytics, setAnalytics] = useState({
    totalForms: 0,
    materials: [],
    campusVisits: [],
    visitTimes: [],
    studentCount: 0,
    facultyCount: 0,
  });

  useEffect(() => {
    fetchAnalyticsData();
  }, []);

  const fetchAnalyticsData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/visitors');
      const { visitors, faculties, visitorArrival } = response.data;

      setAnalytics({
        totalForms: visitors.length,
        materials: processMaterials(visitorArrival),
        campusVisits: processCampusVisits(visitorArrival),
        visitTimes: processVisitTimes(visitorArrival),
        studentCount: visitors.filter(v => v.category.toLowerCase() === 'student').length,
        facultyCount: faculties.length,
      });
    } catch (error) {
      console.error('Error fetching analytics data:', error);
    }
  };

  const processMaterials = (data) => {
    const materials = data.reduce((acc, item) => {
      if (item.materials) {
        acc[item.materials] = (acc[item.materials] || 0) + 1;
      }
      return acc;
    }, {});
    return Object.entries(materials).map(([name, count]) => ({ name, count }));
  };

  const processCampusVisits = (data) => {
    const campuses = data.reduce((acc, item) => {
      if (item.campus) {
        acc[item.campus] = (acc[item.campus] || 0) + 1;
      }
      return acc;
    }, {});
    return Object.entries(campuses).map(([name, count]) => ({ name, count }));
  };

  const processVisitTimes = (data) => {
    const times = data.reduce((acc, item) => {
      if (item.visit_time) {
        const hour = new Date(`2000-01-01T${item.visit_time}`).getHours();
        const timeOfDay = hour < 12 ? 'Morning' : hour < 17 ? 'Afternoon' : 'Evening';
        acc[timeOfDay] = (acc[timeOfDay] || 0) + 1;
      }
      return acc;
    }, {});
    return Object.entries(times).map(([name, count]) => ({ name, count }));
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Analytics',
      },
    },
  };

  const materialChartOptions = {
    ...chartOptions,
    plugins: {
      ...chartOptions.plugins,
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 10,
            weight: 'bold',
          },
          padding: 10,
          boxWidth: 80,
          color: '#333',
          generateLabels: (chart) => {
            const datasets = chart.data.datasets;
            return chart.data.labels.map((label, index) => ({
              text: `${label}: ${datasets[0].data[index]}`,
              fillStyle: datasets[0].backgroundColor[index],
              hidden: isNaN(datasets[0].data[index]),
              index: index
            }));
          }
        }
      },
      tooltip: {
        callbacks: {
          label: (context) => `${context.label}: ${context.formattedValue}`
        }
      }
    }
  };

  return (
    <div className="analytics-page bg-gray-100 p-6 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Visit Analytics</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Total Forms Submitted</h2>
          <p className="text-4xl font-bold text-blue-600">{analytics.totalForms}</p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Materials Used</h2>
          <div className="h-64 overflow-y-auto">
          <div className="w-full h-full">
            <Pie data={{
              labels: analytics.materials.map(item => item.name),
              datasets: [{
                data: analytics.materials.map(item => item.count),
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
              }],
            }} options={materialChartOptions} />
          </div>
        </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Campus Visits</h2>
          <div className="h-64">
            <Doughnut data={{
              labels: analytics.campusVisits.map(item => item.name),
              datasets: [{
                data: analytics.campusVisits.map(item => item.count),
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
              }],
            }} options={chartOptions} />
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Visit Times</h2>
          <div className="h-64">
            <Bar data={{
              labels: analytics.visitTimes.map(item => item.name),
              datasets: [{
                label: 'Visit Times',
                data: analytics.visitTimes.map(item => item.count),
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
              }],
            }} options={chartOptions} />
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Visitor Types</h2>
          <div className="flex justify-around">
            <div className="text-center">
              <p className="text-3xl font-bold text-green-600">{analytics.studentCount}</p>
              <p className="text-gray-600">Students</p>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-purple-600">{analytics.facultyCount}</p>
              <p className="text-gray-600">Faculty</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnalyticsPage;
