import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie, Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

const AnalyticsPage = () => {
  const [analytics, setAnalytics] = useState({
    totalForms: 0,
    materials: [],
    campusVisits: [],
    visitTimes: [],
    studentCount: 0,
    facultyCount: 0,
  });

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      const [visitors, materials, campus, visitTimes] = await Promise.all([
        axios.get('http://localhost:5000/visitors'),
        axios.get('http://localhost:5000/analytics/materials'),
        axios.get('http://localhost:5000/analytics/campus'),
        axios.get('http://localhost:5000/analytics/visit-times'),
      ]);

      setAnalytics({
        totalForms: visitors.data.visitors.length,
        materials: materials.data,
        campusVisits: campus.data,
        visitTimes: visitTimes.data,
        studentCount: visitors.data.visitors.filter(v => v.category === 'Student').length,
        facultyCount: visitors.data.faculties.length,
      });
    } catch (error) {
      console.error('Error fetching analytics:', error);
    }
  };

  const materialsChartData = {
    labels: analytics.materials.map(item => item.materials),
    datasets: [{
      data: analytics.materials.map(item => item.count),
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
    }],
  };

  const campusChartData = {
    labels: analytics.campusVisits.map(item => item.campus),
    datasets: [{
      data: analytics.campusVisits.map(item => item.count),
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
    }],
  };

  const visitTimesChartData = {
    labels: analytics.visitTimes.map(item => item.time_of_day),
    datasets: [{
      label: 'Visit Times',
      data: analytics.visitTimes.map(item => item.count),
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
    }],
  };
  const visitTimesChartOptions = {
    maintainAspectRatio: false,
    indexAxis: 'y',
    scales: {
      x: {
        beginAtZero: true,
        ticks: {
          font: {
            size: 14,
            weight: 'bold',
          },
        },
      },
      y: {
        ticks: {
          font: {
            size: 16,
            weight: 'bold',
          },
        },
      },
    },
    plugins: {
      legend: {
        display: false,
      },
    },
  };
  

  return (
    <div className="analytics-page bg-gray-100 p-6 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Visit Analytics</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Total Forms Submitted</h2>
          <p className="text-4xl font-bold text-blue-600">{analytics.totalForms}</p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Common Materials</h2>
          <div className="h-64">
            <Pie data={materialsChartData} options={{ maintainAspectRatio: false }} />
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Campus Visits</h2>
          <div className="h-64">
            <Doughnut data={campusChartData} options={{ maintainAspectRatio: false }} />
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Preferred Visit Times</h2>
          <div className="h-64">
            <Bar data={visitTimesChartData} options={visitTimesChartOptions} />
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Visitor Types</h2>
          <div className="flex justify-around">
            <div className="text-center">
              <p className="text-3xl font-bold text-green-600">{analytics.studentCount}</p>
              <p className="text-gray-600">Students</p>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-purple-600">{analytics.facultyCount}</p>
              <p className="text-gray-600">Faculty</p>
            </div>
          </div>
        </div>
      </div>
    </div>);
};

export default AnalyticsPage;
