import React, { useState, useEffect } from 'react';
import { Bar, Pie, Line } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title, PointElement, LineElement } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title, PointElement, LineElement);

const Analytics = () => {
  const [analyticsData, setAnalyticsData] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/analytics')
      .then(response => response.json())
      .then(data => setAnalyticsData(data))
      .catch(error => console.error('Error fetching analytics:', error));
  }, []);

  if (!analyticsData) return <div>Loading analytics...</div>;

  const visitorGenderData = {
    labels: ['Male', 'Female'],
    datasets: [{
      data: [analyticsData.visitors.male_visitors, analyticsData.visitors.female_visitors],
      backgroundColor: ['rgba(54, 162, 235, 0.6)', 'rgba(255, 99, 132, 0.6)'],
    }]
  };

  const visitorArrivalData = {
    labels: ['Total Arrivals', 'Unique Campuses'],
    datasets: [{
      label: 'Visitor Arrival Statistics',
      data: [analyticsData.visitorArrival.total_arrivals, analyticsData.visitorArrival.unique_campuses],
      backgroundColor: ['rgba(75, 192, 192, 0.6)', 'rgba(153, 102, 255, 0.6)'],
    }]
  };

  return (
    <div>
      <h2>Visitor Analytics Dashboard</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Visitor Statistics</h3>
          <p>Total Visitors: {analyticsData.visitors.total_visitors}</p>
          <p>Unique Visitors: {analyticsData.visitors.unique_visitors}</p>
          <Pie data={visitorGenderData} options={{ responsive: true, plugins: { title: { display: true, text: 'Visitor Gender Distribution' } } }} />
        </div>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Faculty Statistics</h3>
          <p>Total Faculties: {analyticsData.faculties.total_faculties}</p>
          <p>Unique Faculties: {analyticsData.faculties.unique_faculties}</p>
        </div>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Group In-Charge Statistics</h3>
          <p>Total Group In-Charge: {analyticsData.groupInCharge.total_group_incharge}</p>
          <p>Unique Positions: {analyticsData.groupInCharge.unique_positions}</p>
        </div>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Visitor Arrival Statistics</h3>
          <Bar data={visitorArrivalData} options={{ responsive: true }} />
          <p>Average Time Difference: {analyticsData.visitorArrival.avg_time_difference.toFixed(2)} hours</p>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
