import React, { useState, useEffect } from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Analytics = () => {
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/analytics')
      .then(response => response.json())
      .then(data => {
        setAnalyticsData(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching analytics:', error);
        setError('Failed to load analytics data');
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading analytics...</div>;
  if (error) return <div>{error}</div>;
  if (!analyticsData || !analyticsData.commonMaterials || !analyticsData.preferredTimings) {
    return <div>No analytics data available</div>;
  }

  const materialData = {
    labels: analyticsData.commonMaterials.map(item => item.materials),
    datasets: [{
      data: analyticsData.commonMaterials.map(item => item.count),
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
    }]
  };

  const timingData = {
    labels: analyticsData.preferredTimings.map(item => item.visit_time),
    datasets: [{
      label: 'Preferred Timings',
      data: analyticsData.preferredTimings.map(item => item.count),
      backgroundColor: '#36A2EB',
    }]
  };

  return (
    <div>
      <h2>Form Analytics</h2>
      <div>
        <h3>Total Forms Submitted: {analyticsData.totalForms}</h3>
        <h3>Average Group Size: {parseFloat(analyticsData.avgGroupSize).toFixed(2)} students</h3>
      </div>
      <div style={{ width: '50%', margin: '20px auto' }}>
        <h3>Commonly Used Materials</h3>
        <Pie data={materialData} />
      </div>
      <div style={{ width: '70%', margin: '20px auto' }}>
        <h3>Preferred Timings</h3>
        <Bar data={timingData} />
      </div>
    </div>
  );
};

export default Analytics;
