import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSpring, animated, config } from 'react-spring';
import Calendar from './Calendar';
import PublishModal from './PublishModal';
import { ChevronLeft, Send, Calendar as CalendarIcon, Users } from 'lucide-react';

const FormDetailsPage = () => {
  const navigate = useNavigate();

  const [selectedDates, setSelectedDates] = useState([]);
  const [isPublishModalOpen, setIsPublishModalOpen] = useState(false);
  const [publishedLink, setPublishedLink] = useState('');
  const [visitorData, setVisitorData] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [activeTab, setActiveTab] = useState('calendar');

  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: config.molasses
  });

  const slideIn = useSpring({
    transform: 'translateY(0)',
    from: { transform: 'translateY(50px)' },
    config: config.wobbly
  });

  useEffect(() => {
    const fetchVisitorData = async () => {
      try {
        const response = await fetch('http://localhost:5000/visitors');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setVisitorData(data);
      } catch (error) {
        console.error('Error fetching visitor data:', error);
        setFetchError('Error fetching visitor data');
      }
    };
    fetchVisitorData();
  }, []);

  const handleSelectDates = (dates) => {
    setSelectedDates(dates);
  };

  const handlePublish = () => {
    const link = `https://kritikaat.github.io/IPRProject/`;
    setPublishedLink(link);
    setIsPublishModalOpen(true);
  };

  const formatDisplayDate = (dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, parseInt(day) + 1);
    return date.toLocaleDateString();
  };

  return (
    <animated.div style={fadeIn} className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <animated.div style={slideIn} className="bg-white shadow-2xl rounded-lg overflow-hidden">
          <div className="p-6 sm:p-10">
            <div className="flex justify-between items-center mb-8">
              <button
                className="flex items-center px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={() => navigate(-1)}
              >
                <ChevronLeft className="mr-2" size={20} />
                Back
              </button>
              <button
                className="flex items-center px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={handlePublish}
              >
                <Send className="mr-2" size={20} />
                Publish
              </button>
            </div>

            <h1 className="text-4xl font-extrabold text-gray-900 mb-4">Visitor's Form</h1>
            <p className="text-gray-600 mb-8 text-lg">Manage visitor information and schedule.</p>

            <div className="mb-8 flex space-x-4">
              <button
                className={`flex-1 py-2 px-4 rounded-md transition duration-200 ease-in-out ${activeTab === 'calendar' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                onClick={() => setActiveTab('calendar')}
              >
                <CalendarIcon className="inline-block mr-2" size={20} />
                Calendar
              </button>
              <button
                className={`flex-1 py-2 px-4 rounded-md transition duration-200 ease-in-out ${activeTab === 'visitors' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                onClick={() => setActiveTab('visitors')}
              >
                <Users className="inline-block mr-2" size={20} />
                Visitor Data
              </button>
            </div>

            {activeTab === 'calendar' && (
              <div className="mb-12 bg-white p-6 rounded-lg shadow-md">
                <Calendar
                  onSelectDates={handleSelectDates}
                  selectedDates={selectedDates}
                />

                {selectedDates.length > 0 && (
                  <div className="mt-6 p-4 bg-indigo-50 rounded-md border border-indigo-200">
                    <h3 className="text-lg font-semibold text-indigo-800 mb-2">Selected Dates:</h3>
                    <ul className="space-y-1">
                      {selectedDates.map((date) => (
                        <li key={date} className="flex items-center text-indigo-700">
                          <CalendarIcon className="mr-2" size={16} />
                          {formatDisplayDate(date)}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'visitors' && (
              <div className="mb-12">
                <h2 className="text-2xl font-bold text-gray-900 mb-6">Visitor Data</h2>
                
                {fetchError ? (
                  <div className="text-red-600 bg-red-100 p-4 rounded-md shadow">{fetchError}</div>
                ) : (
                  visitorData.length > 0 && (
                    <div className="overflow-x-auto bg-white rounded-lg shadow">
                      <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-gray-50">
                          <tr>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institution Name</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Branch</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Semester</th>
                          </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                          {visitorData.map((visitor) => (
                            <tr key={visitor.id} className="hover:bg-gray-50 transition duration-150 ease-in-out">
                              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{visitor.institution_name}</td>
                              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-600">{visitor.student_branch}</td>
                              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-600">{visitor.student_sem}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )
                )}
              </div>
            )}
          </div>
        </animated.div>
      </div>

      <PublishModal
        isOpen={isPublishModalOpen}
        onClose={() => setIsPublishModalOpen(false)}
        publishedLink={publishedLink}
      />
    </animated.div>
  );
};

export default FormDetailsPage;
