import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AnalyticsPage = () => {
  const [analytics, setAnalytics] = useState({
    totalForms: 0,
    commonMaterials: {},
    campusVisits: {},
    preferredTimes: {},
    studentCount: 0,
    facultyCount: 0,
  });

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      const response = await axios.get('http://localhost:5000/visitors');
      const { visitors, faculties, visitorArrival } = response.data;

      // Process the data to generate analytics
      const totalForms = visitors.length;
      const commonMaterials = processCommonMaterials(visitorArrival);
      const campusVisits = processCampusVisits(visitorArrival);
      const preferredTimes = processPreferredTimes(visitorArrival);
      const studentCount = visitors.filter(v => v.category === 'Student').length;
      const facultyCount = faculties.length;

      setAnalytics({
        totalForms,
        commonMaterials,
        campusVisits,
        preferredTimes,
        studentCount,
        facultyCount,
      });
    } catch (error) {
      console.error('Error fetching analytics:', error);
    }
  };

  // Helper functions to process data
  const processCommonMaterials = (data) => {
    // Process materials data
  };

  const processCampusVisits = (data) => {
    // Process campus visit data
  };

  const processPreferredTimes = (data) => {
    // Process preferred visit times
  };

  return (
    <div className="analytics-page">
      <h1>Visit Analytics</h1>
      <div className="analytics-grid">
        <div className="analytics-item">
          <h2>Total Forms Submitted</h2>
          <p>{analytics.totalForms}</p>
        </div>
        <div className="analytics-item">
          <h2>Common Materials</h2>
          {/* Display common materials */}
        </div>
        <div className="analytics-item">
          <h2>Campus Visits</h2>
          {/* Display campus visit statistics */}
        </div>
        <div className="analytics-item">
          <h2>Preferred Visit Times</h2>
          {/* Display preferred visit times */}
        </div>
        <div className="analytics-item">
          <h2>Visitor Types</h2>
          <p>Students: {analytics.studentCount}</p>
          <p>Faculty: {analytics.facultyCount}</p>
        </div>
      </div>
    </div>
  );
};

export default AnalyticsPage;
