import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from './Calendar';
import PublishModal from './PublishModal';

const FormDetailsPage = () => {
 
  const navigate = useNavigate(); // Hook to programmatically navigate

  const [selectedDates, setSelectedDates] = useState([]);
  const [isPublishModalOpen, setIsPublishModalOpen] = useState(false);
  const [publishedLink, setPublishedLink] = useState('');

  const handleSelectDates = (dates) => {
    setSelectedDates(dates);
  };

  const handlePublish = () => {
    const link = `https://kritikaat.github.io/IPRProject/`;
    setPublishedLink(link);
    setIsPublishModalOpen(true);
  };

  const formatDisplayDate = (dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, parseInt(day) + 1); // Add 1 to the day
    return date.toLocaleDateString();
  };

  return (
    <div className="relative p-6 bg-white shadow-lg rounded-lg max-w mx-auto mt-10">
      <button
        className="absolute top-5 left-5 px-6 py-2 bg-gray-800 text-white rounded-md hover:bg-gray-700 transition duration-200"
        onClick={() => navigate(-1)}
      >
        Back
      </button>

      <button
        className="absolute top-5 right-5 px-5 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-500 transition duration-200 shadow-md"
        onClick={handlePublish}
      >
        Publish
      </button>

      <div className="text-center mt-8">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">
          Visitor's Form {formId}
        </h2>
        <p className="text-gray-600 text-sm">
          Details for Visitor's Form . Select dates below.
        </p>
      </div>

      <Calendar
        onSelectDates={handleSelectDates}
        selectedDates={selectedDates}
      />

      {selectedDates.length > 0 && (
        <div className="mt-8">
          <h3 className="text-lg font-semibold">Selected Dates:</h3>
          <ul className="list-disc list-inside pl-5 mt-2 text-gray-700">
            {selectedDates.map((date) => (
              <li key={date} className="mt-1">
                {formatDisplayDate(date)}
              </li>
            ))}
          </ul>
        </div>
      )}

      <PublishModal
        isOpen={isPublishModalOpen}
        onClose={() => setIsPublishModalOpen(false)}
        publishedLink={publishedLink}
      />
    </div>
  );
};

export default FormDetailsPage;
