import React, { useState, useEffect } from 'react';
import { useSpring, animated } from 'react-spring';
import FormCard from './Formcard';
import SearchBar from './SearchBar';
import { Layout, Menu, Input } from 'lucide-react';
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [selectedMenuItem, setSelectedMenuItem] = useState('Dashboard');

  useEffect(() => {
    setFilteredForms(forms);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: { duration: 500 },
  });

  const slideIn = useSpring({
    transform: 'translateY(0)',
    from: { transform: 'translateY(50px)' },
    config: { mass: 1, tension: 280, friction: 60 },
  });

  const menuItems = [
    { icon: <Layout size={18} />, label: 'Dashboard' },
    { icon: <Input size={18} />, label: 'Analytics' },
    { icon: <Menu size={18} />, label: 'Settings' },
  ];

  return (
    <animated.div style={fadeIn} className="flex h-screen bg-gray-100">
      <Card className="w-64 bg-white shadow-lg">
        <CardContent className="p-5">
          <h2 className="mb-5 text-xl font-bold text-gray-800">Form Manager</h2>
          <nav>
            {menuItems.map((item) => (
              <Button
                key={item.label}
                variant={selectedMenuItem === item.label ? "secondary" : "ghost"}
                className="w-full justify-start mb-2"
                onClick={() => setSelectedMenuItem(item.label)}
              >
                {item.icon}
                <span className="ml-2">{item.label}</span>
              </Button>
            ))}
          </nav>
        </CardContent>
      </Card>

      <animated.div style={slideIn} className="flex-1 p-6 overflow-auto">
        <Card className="mb-8 w-full mx-auto">
          <CardContent className="p-4">
            <SearchBar
              onSearch={handleSearch}
              className="w-full"
            />
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {filteredForms.length > 0 ? (
            filteredForms.map((form) => (
              <animated.div key={form.id} style={fadeIn}>
                <FormCard form={form} />
              </animated.div>
            ))
          ) : (
            Array.from({ length: 8 }).map((_, index) => (
              <Card key={index}>
                <CardContent className="p-4">
                  <Skeleton className="h-4 w-3/4 mb-2" />
                  <Skeleton className="h-4 w-1/2" />
                </CardContent>
              </Card>
            ))
          )}
        </div>
      </animated.div>
    </animated.div>
  );
};

export default Dashboard;
