import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Send, Filter, Download, Trash2, Search } from 'lucide-react';

const FeedbackDetailsPage = () => {
  const navigate = useNavigate();
  const [feedbacks, setFeedbacks] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [ratingSearchTerm, setRatingSearchTerm] = useState('');
  const [selectedFeedbacks, setSelectedFeedbacks] = useState([]);
  const [selectedRatings, setSelectedRatings] = useState([]);
  const [activeTab, setActiveTab] = useState('feedback');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [feedbackResponse, ratingsResponse] = await Promise.all([
          axios.get(`http://localhost:3000/feedback`),
          axios.get(`http://localhost:3000/ratings`)
        ]);
        setFeedbacks(feedbackResponse.data);
        setRatings(ratingsResponse.data);
      } catch (error) {
        setError(`Error fetching data: ${error.message}`);
        console.error(error); // Log the error for debugging
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString();
  };

  const handleFeedbackSelection = (id) => {
    setSelectedFeedbacks((prev) => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleRatingSelection = (id) => {
    setSelectedRatings((prev) => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelectedFeedbacks = () => {
    setFeedbacks((prev) => prev.filter(item => !selectedFeedbacks.includes(item.id)));
    setSelectedFeedbacks([]);
  };

  const handleDeleteSelectedRatings = () => {
    setRatings((prev) => prev.filter(item => !selectedRatings.includes(item.id)));
    setSelectedRatings([]);
  };

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="flex flex-col items-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        <p className="mt-4">Loading data, please wait...</p>
      </div>
    </div>
  );

  if (error) return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative">
        {error}
      </div>
    </div>
  );

  const filteredFeedbacks = feedbacks.filter(feedback =>
    feedback.institutionName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    feedback.website.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredRatings = ratings.filter(rating =>
    rating.institutionName.toLowerCase().includes(ratingSearchTerm.toLowerCase()) ||
    rating.rating.toString().includes(ratingSearchTerm.toLowerCase()) // Adjust criteria as needed
  );

  // Tab content
  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8 flex justify-between items-center">
        <div>
          <button
            onClick={() => navigate('/')}
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg flex items-center"
          >
            Back to Dashboard
          </button>
        </div>
      </div>

      <h1 className="text-4xl font-bold text-gray-800 mb-6">Feedback and Ratings</h1>
      <p className="text-gray-600 mb-8 text-lg">Manage feedback and rating information.</p>

      {/* Custom Tab Navigation */}
      <div className="mb-6">
        <div className="border-b border-gray-200">
          <div className="flex space-x-8">
            <button
              onClick={() => setActiveTab('feedback')}
              className={`py-4 px-1 relative ${
                activeTab === 'feedback'
                  ? 'text-blue-600 border-b-2 border-blue-600 font-medium'
                  : 'text-gray-500 hover:text-gray-700'
              } transition-colors duration-200`}
            >
              Feedback
              <span className={`absolute bottom-0 left-0 w-full h-0.5 transform transition-transform duration-200 ${
                activeTab === 'feedback' ? 'scale-x-100' : 'scale-x-0'
              } bg-blue-600`}></span>
            </button>
            <button
              onClick={() => setActiveTab('ratings')}
              className={`py-4 px-1 relative ${
                activeTab === 'ratings'
                  ? 'text-blue-600 border-b-2 border-blue-600 font-medium'
                  : 'text-gray-500 hover:text-gray-700'
              } transition-colors duration-200`}
            >
              Ratings
              <span className={`absolute bottom-0 left-0 w-full h-0.5 transform transition-transform duration-200 ${
                activeTab === 'ratings' ? 'scale-x-100' : 'scale-x-0'
              } bg-blue-600`}></span>
            </button>
          </div>
        </div>
      </div>

      {/* Tab Content */}
      <div className="relative">
        <div className={`transition-opacity duration-300 ${activeTab === 'feedback' ? 'opacity-100' : 'opacity-0 absolute top-0 left-0 w-full'}`}>
          {activeTab === 'feedback' && (
            <div className="space-y-6">
              <div className="flex justify-between items-center mb-6">
                <div className="flex space-x-2">
                  <div className="relative">
                    <input
                      type="text"
                      placeholder="Search feedback..."
                      className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
                  </div>
                  <button className="p-2 bg-blue-100 text-blue-700 rounded-md hover:bg-blue-200 transition duration-300">
                    <Filter size={20} />
                  </button>
                  <button className="p-2 bg-green-100 text-green-700 rounded-md hover:bg-green-200 transition duration-300">
                    <Download size={20} />
                  </button>
                  {selectedFeedbacks.length > 0 && (
                    <button
                      onClick={handleDeleteSelectedFeedbacks}
                      className="p-2 bg-red-100 text-red-700 rounded-md hover:bg-red-200 transition duration-300"
                    >
                      <Trash2 size={20} />
                    </button>
                  )}
                </div>
              </div>

              <div className="overflow-x-auto bg-white rounded-lg shadow hover:shadow-lg transition-shadow duration-300">
                <table className="min-w-full divide-y divide-gray-200">
                  <TableHeader
                    headers={['Institution', 'Website', 'Visit Date', 'Staff Name', 'Email', 'Mobile', 'Students']}
                    selectedItems={selectedFeedbacks}
                    items={filteredFeedbacks}
                    onSelectAll={() => {
                      if (selectedFeedbacks.length === filteredFeedbacks.length) {
                        setSelectedFeedbacks([]);
                      } else {
                        setSelectedFeedbacks(filteredFeedbacks.map(item => item.id));
                      }
                    }}
                  />
                  <tbody className="bg-white divide-y divide-gray-200">
                    {filteredFeedbacks.map((feedback, index) => (
                      <tr 
                        key={feedback.id} 
                        className={`hover:bg-gray-100 ${selectedFeedbacks.includes(feedback.id) ? 'bg-blue-100' : ''}`}
                        onClick={() => handleFeedbackSelection(feedback.id)}
                      >
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.institutionName}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.website}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{formatDate(feedback.visitDate)}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.staffName}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.email}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.mobile}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.students}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>

        <div className={`transition-opacity duration-300 ${activeTab === 'ratings' ? 'opacity-100' : 'opacity-0 absolute top-0 left-0 w-full'}`}>
          {activeTab === 'ratings' && (
            <div className="space-y-6">
              <div className="flex justify-between items-center mb-6">
                <div className="flex space-x-2">
                  <div className="relative">
                    <input
                      type="text"
                      placeholder="Search ratings..."
                      className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      value={ratingSearchTerm}
                      onChange={(e) => setRatingSearchTerm(e.target.value)}
                    />
                    <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
                  </div>
                  {selectedRatings.length > 0 && (
                    <button
                      onClick={handleDeleteSelectedRatings}
                      className="p-2 bg-red-100 text-red-700 rounded-md hover:bg-red-200 transition duration-300"
                    >
                      <Trash2 size={20} />
                    </button>
                  )}
                </div>
              </div>

              <div className="overflow-x-auto bg-white rounded-lg shadow hover:shadow-lg transition-shadow duration-300">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-200">
                    <tr>
                      <th className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        <input 
                          type="checkbox" 
                          checked={selectedRatings.length === filteredRatings.length} 
                          onChange={() => {
                            if (selectedRatings.length === filteredRatings.length) {
                              setSelectedRatings([]);
                            } else {
                              setSelectedRatings(filteredRatings.map(item => item.id));
                            }
                          }} 
                        />
                      </th>
                      <th className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">Institution</th>
                      <th className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">Rating</th>
                      <th className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">Comments</th>
                      <th className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">Date</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {filteredRatings.map((rating) => (
                      <tr 
                        key={rating.id} 
                        className={`hover:bg-gray-100 ${selectedRatings.includes(rating.id) ? 'bg-blue-100' : ''}`}
                        onClick={() => handleRatingSelection(rating.id)}
                      >
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          <input 
                            type="checkbox" 
                            checked={selectedRatings.includes(rating.id)} 
                            onChange={() => handleRatingSelection(rating.id)} 
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{rating.institutionName}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{rating.rating}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{rating.comments}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{formatDate(rating.date)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Separate TableHeader component for better code organization
const TableHeader = ({ headers, selectedItems, items, onSelectAll }) => (
  <thead className="bg-gray-200">
    <tr>
      <th className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
        <input type="checkbox" checked={selectedItems.length === items.length} onChange={onSelectAll} />
      </th>
      {headers.map((header) => (
        <th key={header} className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{header}</th>
      ))}
    </tr>
  </thead>
);

export default FeedbackDetailsPage;
