import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from './Calendar';
import PublishModal from './PublishModal';

const FormDetailsPage = () => {
  const navigate = useNavigate();

  const [selectedDates, setSelectedDates] = useState([]);
  const [isPublishModalOpen, setIsPublishModalOpen] = useState(false);
  const [publishedLink, setPublishedLink] = useState('');
  
  // State to store fetched data
  const [visitorData, setVisitorData] = useState([]);
  const [fetchError, setFetchError] = useState(null);

  // Fetch visitor data on component mount
  useEffect(() => {
    const fetchVisitorData = async () => {
      try {
        const response = await fetch('http://localhost:5000/visitors');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setVisitorData(data);
      } catch (error) {
        console.error('Error fetching visitor data:', error);
        setFetchError('Error fetching visitor data');
      }
    };

    fetchVisitorData();
  }, []);

  // ... other functions ...

  return (
    <div className="relative p-6 bg-white shadow-lg rounded-lg max-w mx-auto mt-10 ml-2 mr-2 mt-4">
      {/* ... other JSX elements ... */}

      {/* Display fetched visitor data */}
      {fetchError ? (
        <div className="text-red-600 mt-4">Error: {fetchError}</div>
      ) : (
        visitorData.length > 0 && (
          <div className="mt-10">
            <h3 className="text-lg font-semibold mb-4">Fetched Visitor Data:</h3>
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white">
                <thead className="bg-gray-800 text-white">
                  <tr>
                    <th className="py-2 px-4 text-left">Institution Name</th>
                    <th className="py-2 px-4 text-left">Student Branch</th>
                    <th className="py-2 px-4 text-left">Student Semester</th>
                  </tr>
                </thead>
                <tbody className="text-gray-700">
                  {visitorData.map((visitor, index) => (
                    <tr key={visitor.id} className={index % 2 === 0 ? "bg-gray-100" : ""}>
                      <td className="py-2 px-4">{visitor.institution_name}</td>
                      <td className="py-2 px-4">{visitor.student_branch}</td>
                      <td className="py-2 px-4">{visitor.student_sem}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )
      )}

      {/* ... other JSX elements ... */}
    </div>
  );
};

export default FormDetailsPage;
