import React, { useState, useEffect } from 'react';

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [activeTab, setActiveTab] = useState('Forms');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    setFilteredForms(forms);
  }, [forms]);

  const handleSearch = (event) => {
    const query = event.target.value.toLowerCase();
    setSearchQuery(query);
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query)
    );
    setFilteredForms(filtered);
  };

  const tabs = ['Forms', 'Analytics'];

  const renderContent = () => {
    switch (activeTab) {
      case 'Forms':
        return (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredForms.map((form) => (
              <div key={form.id} className="bg-white rounded-lg shadow-md p-6">
                <h3 className="text-xl font-semibold mb-2">{form.title}</h3>
                <p className="text-gray-600 mb-4">{form.description}</p>
                <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors">
                  Edit Form
                </button>
              </div>
            ))}
          </div>
        );
      case 'Analytics':
        return <h2 className="text-2xl font-bold">Analytics Coming Soon</h2>;
      default:
        return null;
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <h1 className="text-2xl font-bold text-gray-900">Form Manager</h1>
            <div className="flex items-center">
              <input
                type="text"
                placeholder="Search forms..."
                value={searchQuery}
                onChange={handleSearch}
                className="px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button className="ml-4 bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors">
                New Form
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="flex flex-1 overflow-hidden">
        <nav className="w-64 bg-blue-800 text-white p-6">
          <ul className="space-y-2">
            {tabs.map((tab) => (
              <li 
                key={tab} 
                className={`py-2 px-4 rounded cursor-pointer transition-colors ${
                  activeTab === tab ? 'bg-blue-600' : 'hover:bg-blue-700'
                }`}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
              </li>
            ))}
          </ul>
        </nav>

        <main className="flex-1 p-8 overflow-auto">
          {renderContent()}
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
