import React, { useState, useEffect } from 'react';
import FormCard from './Formcard';
import SearchBar from './SearchBar';

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);

  useEffect(() => {
    // When forms are received, set them to filteredForms
    setFilteredForms(forms);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms); // Reset to all forms if query is empty
      return;
    }

    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-gray-900 p-6 flex flex-col text-white shadow-lg">
        <h2 className="mb-6 text-2xl font-semibold border-b border-gray-700 pb-4">Form Manager</h2>
        <ul className="space-y-3">
          <li className="py-2 px-4 cursor-pointer hover:bg-gray-800 rounded-md transition-colors">
            Dashboard
          </li>
          <li className="py-2 px-4 cursor-pointer hover:bg-gray-800 rounded-md transition-colors">
            Analytics
          </li>
          <li className="py-2 px-4 cursor-pointer hover:bg-gray-800 rounded-md transition-colors">
            Settings
          </li>
        </ul>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 p-8 flex flex-col space-y-6">
        {/* Search Bar */}
        <div className="max-w-4xl w-full mx-auto">
          <SearchBar
            onSearch={handleSearch}
            className="w-full p-3 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500"
          />
        </div>

        {/* Form Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredForms.length > 0 ? (
            filteredForms.map((form) => (
              <FormCard key={form.id} form={form} />
            ))
          ) : (
            <p className="text-center text-lg text-gray-500">No forms available or loading...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
