import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Send, Filter, Download, Trash2, Search, ChevronUp, ChevronDown } from 'lucide-react';

const FeedbackDetailsPage = () => {
  const navigate = useNavigate();
  const [feedbacks, setFeedbacks] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFeedbacks, setSelectedFeedbacks] = useState([]);
  const [selectedRatings, setSelectedRatings] = useState([]);
  const [isFeedbackExpanded, setIsFeedbackExpanded] = useState(true);
  const [isRatingsExpanded, setIsRatingsExpanded] = useState(true);
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    setFadeIn(true);
    const fetchData = async () => {
      setLoading(true);
      try {
        const [feedbackResponse, ratingsResponse] = await Promise.all([
          axios.get(`http://localhost:3000/feedback`),
          axios.get(`http://localhost:3000/ratings`)
        ]);
        setFeedbacks(feedbackResponse.data);
        setRatings(ratingsResponse.data);
      } catch (error) {
        setError(`Error fetching data: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString();
  };

  const handleFeedbackSelection = (id) => {
    setSelectedFeedbacks(prev => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleRatingSelection = (id) => {
    setSelectedRatings(prev => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelectedFeedbacks = () => {
    setFeedbacks(prev => prev.filter(item => !selectedFeedbacks.includes(item.id)));
    setSelectedFeedbacks([]);
  };

  const handleDeleteSelectedRatings = () => {
    setRatings(prev => prev.filter(item => !selectedRatings.includes(item.id)));
    setSelectedRatings([]);
  };

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen bg-gray-50">
      <div className="flex flex-col items-center space-y-4">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-blue-500"></div>
        <p className="text-gray-600 font-medium animate-pulse">Loading data...</p>
      </div>
    </div>
  );

  if (error) return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-md shadow-md animate-fade-in">
        <p className="font-medium">Error</p>
        <p>{error}</p>
      </div>
    </div>
  );

  return (
    <div className={`min-h-screen bg-gray-50 transition-opacity duration-500 ease-in-out ${fadeIn ? 'opacity-100' : 'opacity-0'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="mb-8 flex justify-between items-center">
          <button
            onClick={() => navigate('/')}
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-lg transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg flex items-center space-x-2 group"
          >
            <span className="transform transition-transform group-hover:-translate-x-1">‚Üê</span>
            <span>Back to Dashboard</span>
          </button>
        </div>

        <div className="space-y-6 mb-12">
          <h1 className="text-5xl font-bold text-gray-800 mb-4 animate-fade-in">
            Feedback and Ratings
          </h1>
          <p className="text-gray-600 text-xl animate-fade-in delay-200">
            Comprehensive overview of all feedback and ratings.
          </p>
        </div>

        {/* Feedback Section */}
        <div className="bg-white rounded-xl shadow-md overflow-hidden mb-12 transition-all duration-300">
          <div 
            className="px-6 py-4 border-b border-gray-200 flex justify-between items-center cursor-pointer hover:bg-gray-50"
            onClick={() => setIsFeedbackExpanded(!isFeedbackExpanded)}
          >
            <h2 className="text-2xl font-bold text-gray-900 flex items-center space-x-2">
              <span>Feedback</span>
              {isFeedbackExpanded ? <ChevronUp size={24} /> : <ChevronDown size={24} />}
            </h2>
            <div className="flex space-x-3">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search feedback..."
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
              </div>
              <button className="p-2 bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition duration-300 hover:shadow-md">
                <Filter size={20} />
              </button>
              <button className="p-2 bg-green-100 text-green-700 rounded-lg hover:bg-green-200 transition duration-300 hover:shadow-md">
                <Download size={20} />
              </button>
              {selectedFeedbacks.length > 0 && (
                <button
                  onClick={handleDeleteSelectedFeedbacks}
                  className="p-2 bg-red-100 text-red-700 rounded-lg hover:bg-red-200 transition duration-300 hover:shadow-md animate-fade-in"
                >
                  <Trash2 size={20} />
                </button>
              )}
            </div>
          </div>

          <div className={`transition-all duration-300 ease-in-out ${isFeedbackExpanded ? 'max-h-[2000px] opacity-100' : 'max-h-0 opacity-0 overflow-hidden'}`}>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      <input
                        type="checkbox"
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded transition-all duration-200"
                        checked={selectedFeedbacks.length === feedbacks.length}
                        onChange={() => {
                          if (selectedFeedbacks.length === feedbacks.length) {
                            setSelectedFeedbacks([]);
                          } else {
                            setSelectedFeedbacks(feedbacks.map(item => item.id));
                          }
                        }}
                      />
                    </th>
                    {['Institution', 'Website', 'Visit Date', 'Staff Name', 'Email', 'Mobile', 'Students'].map(header => (
                      <th key={header} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        {header}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {feedbacks.map((feedback, index) => (
                    <tr 
                      key={index} 
                      className={`${index % 2 === 0 ? 'bg-white' : 'bg-gray-50'} hover:bg-blue-50 transition-colors duration-150 ease-in-out`}
                    >
                      <td className="px-6 py-4 whitespace-nowrap">
                        <input
                          type="checkbox"
                          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded transition-all duration-200"
                          checked={selectedFeedbacks.includes(feedback.id)}
                          onChange={() => handleFeedbackSelection(feedback.id)}
                        />
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.institutionName || 'N/A'}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.website || 'N/A'}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{formatDate(feedback.visitDate)}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.staffName || 'N/A'}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.staffEmail || 'N/A'}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.staffMobile || 'N/A'}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{feedback.totalStudents || 'N/A'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {/* Ratings Section */}
        <div className="bg-white rounded-xl shadow-md overflow-hidden transition-all duration-300">
          <div 
            className="px-6 py-4 border-b border-gray-200 flex justify-between items-center cursor-pointer hover:bg-gray-50"
            onClick={() => setIsRatingsExpanded(!isRatingsExpanded)}
          >
            <h2 className="text-2xl font-bold text-gray-900 flex items-center space-x-2">
              <span>Ratings</span>
              {isRatingsExpanded ? <ChevronUp size={24} /> : <ChevronDown size={24} />}
            </h2>
            <div className="flex space-x-3">
              <button className="p-2 bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition duration-300 hover:shadow-md">
                <Filter size={20} />
              </button>
              <button className="p-2 bg-green-100 text-green-700 rounded-lg hover:bg-green-200 transition duration-300 hover:shadow-md">
                <Download size={20} />
              </button>
              {selectedRatings.length > 0 && (
                <button
                  onClick={handleDeleteSelectedRatings}
                  className="p-2 bg-red-100 text-red-700 rounded-lg hover:bg-red-200 transition duration-300 hover:shadow-md animate-fade-in"
                >
                  <Trash2 size={20} />
                </button>
              )}
            </div>
          </div>

          <div className={`transition-all duration-300 ease-in-out ${isRatingsExpanded ? 'max-h-[2000px] opacity-100' : 'max-h-0 opacity-0 overflow-hidden'}`}>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                {/* ... Rest of the ratings table code remains the same ... */}
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Add these styles to your global CSS or Tailwind config
const styles = `
  @keyframes fade-in {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  .animate-fade-in {
    animation: fade-in 0.5s ease-out forwards;
  }

  .delay-200 {
    animation-delay: 200ms;
  }
`;

export default FeedbackDetailsPage;
