import React, { useState, useEffect } from 'react';
import FormCard from './Formcard';
import SearchBar from './SearchBar';

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);

  useEffect(() => {
    setFilteredForms(forms); // Initialize with all forms
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms); // Reset to all forms if search query is empty
      return;
    }

    // Filter forms based on query
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  return (
    <div className="flex h-screen bg-gray-200">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 p-5 text-white">
        <h2 className="mb-5 text-lg">Form Manager</h2>
        <ul className="list-none p-0">
          <li className="py-2 cursor-pointer hover:bg-gray-700 pl-2">Dashboard</li>
          <li className="py-2 cursor-pointer hover:bg-gray-700 pl-2">Analytics</li>
          <li className="py-2 cursor-pointer hover:bg-gray-700 pl-2">Settings</li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-6 text-gray-700">
        {/* Search Bar */}
        <div className="mb-8">
          <SearchBar
            onSearch={handleSearch}
            className="max-w-2xl mx-auto" // Wider search bar in the center
          />
        </div>

        {/* Form Cards */}
        <div className="grid grid-cols-[repeat(auto-fill,minmax(250px,1fr))] gap-8">
          {filteredForms.length > 0 ? (
            filteredForms.map((form) => (
              <FormCard key={form.id} form={form} />
            ))
          ) : (
            <p>Loading Forms....</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
