import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Bar, Pie, Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

const AnalyticsPage = () => {
  const [analytics, setAnalytics] = useState({
    totalForms: 0,
    materials: [],
    campusVisits: [],
    visitTimes: [],
    studentCount: 0,
    facultyCount: 0,
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchAnalyticsData = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);
      const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/visitors`);
      const { visitors, faculties, visitorArrival } = data;

      setAnalytics({
        totalForms: visitors.length,
        materials: processData(visitorArrival, 'materials'),
        campusVisits: processData(visitorArrival, 'campus'),
        visitTimes: processVisitTimes(visitorArrival),
        studentCount: visitors.filter(v => v.category.toLowerCase() === 'student').length,
        facultyCount: faculties.length,
      });
    } catch (error) {
      console.error('Error fetching analytics data:', error);
      setError('Failed to fetch analytics data. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchAnalyticsData();
  }, [fetchAnalyticsData]);

  const processData = (data, key) => {
    const processed = data.reduce((acc, item) => {
      if (item[key]) {
        acc[item[key]] = (acc[item[key]] || 0) + 1;
      }
      return acc;
    }, {});
    return Object.entries(processed).map(([name, count]) => ({ name, count }));
  };

  const processVisitTimes = (data) => {
    const times = data.reduce((acc, item) => {
      if (item.visit_time) {
        const hour = new Date(`2000-01-01T${item.visit_time}`).getHours();
        const timeOfDay = hour < 12 ? 'Morning' : hour < 17 ? 'Afternoon' : 'Evening';
        acc[timeOfDay] = (acc[timeOfDay] || 0) + 1;
      }
      return acc;
    }, {});
    return Object.entries(times).map(([name, count]) => ({ name, count }));
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Analytics',
      },
    },
  };

  const ChartCard = ({ title, chartType, data, options = chartOptions }) => {
    const ChartComponent = chartType === 'Bar' ? Bar : chartType === 'Pie' ? Pie : Doughnut;
    return (
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-xl font-semibold text-gray-700 mb-3">{title}</h2>
        <div className="h-64">
          <ChartComponent 
            data={{
              labels: data.map(item => item.name),
              datasets: [{
                data: data.map(item => item.count),
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
              }],
            }} 
            options={options} 
          />
        </div>
      </div>
    );
  };

  const StatCard = ({ title, value, color }) => (
    <div className="bg-white p-4 rounded-lg shadow">
      <h2 className="text-xl font-semibold text-gray-700 mb-3">{title}</h2>
      <p className={`text-4xl font-bold text-${color}-600`}>{value}</p>
    </div>
  );

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-gray-900"></div>
      </div>
    );
  }

  if (error) {
    return (
      <Alert variant="destructive">
        <AlertTitle>Error</AlertTitle>
        <AlertDescription>{error}</AlertDescription>
      </Alert>
    );
  }

  return (
    <div className="analytics-page bg-gray-100 p-6 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Visit Analytics</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <StatCard title="Total Forms Submitted" value={analytics.totalForms} color="blue" />
        <ChartCard title="Materials Used" chartType="Pie" data={analytics.materials} />
        <ChartCard title="Campus Visits" chartType="Doughnut" data={analytics.campusVisits} />
        <ChartCard title="Visit Times" chartType="Bar" data={analytics.visitTimes} />
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-3">Visitor Types</h2>
          <div className="flex justify-around">
            <StatCard title="Students" value={analytics.studentCount} color="green" />
            <StatCard title="Faculty" value={analytics.facultyCount} color="purple" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnalyticsPage;
