import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Menu } from 'lucide-react';
import FormCard from './Formcard';
import SearchBar from './SearchBar';

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [pages, setPages] = useState(['Dashboard', 'Analytics', 'Settings']);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    setFilteredForms(forms);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const newPages = Array.from(pages);
    const [reorderedItem] = newPages.splice(result.source.index, 1);
    newPages.splice(result.destination.index, 0, reorderedItem);
    setPages(newPages);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <div className={`${isSidebarOpen ? 'w-64' : 'w-16'} transition-all duration-300 ease-in-out bg-indigo-800 text-white`}>
        <div className="p-4 flex justify-between items-center">
          <h2 className={`${isSidebarOpen ? 'block' : 'hidden'} text-xl font-bold`}>Form Manager</h2>
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} className="text-white">
            <Menu size={24} />
          </button>
        </div>
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="sidebar">
            {(provided) => (
              <ul {...provided.droppableProps} ref={provided.innerRef} className="list-none p-0">
                {pages.map((page, index) => (
                  <Draggable key={page} draggableId={page} index={index}>
                    {(provided) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="py-3 px-4 cursor-pointer hover:bg-indigo-700 transition-colors duration-200"
                      >
                        {isSidebarOpen ? page : page[0]}
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      </div>

      <div className="flex-1 p-8 overflow-auto">
        <div className="mb-8 max-w-2xl mx-auto">
          <SearchBar onSearch={handleSearch} className="w-full" />
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {filteredForms.length > 0 ? (
            filteredForms.map((form) => (
              <FormCard key={form.id} form={form} />
            ))
          ) : (
            <p className="text-center col-span-full text-gray-500 text-lg">Loading Forms...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
