import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import VisitorDetailsPage from './visitorsDetailpage';
import ExhibitionDetails from './exhibitondetailpage';
import FeedbackDetails from './feedbackdetailspage';
import ScienceDayDetails from './sciencedaydetails';
import { fetchForms } from '../services/api'; // Import the fetchForms function from your API file

const FormDetailsPage = () => {
  const navigate = useNavigate();
  const { formId } = useParams();
  const [formTitle, setFormTitle] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getFormDetails = async () => {
      setIsLoading(true);
      try {
        const forms = await fetchForms();
        const form = forms.find(f => f.id === parseInt(formId));
        if (!form) {
          throw new Error('Form not found');
        }
        setFormTitle(form.title);
      } catch (error) {
        console.error('Error fetching form data:', error);
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    getFormDetails();
  }, [formId]);

  const renderFormContent = () => {
    switch (formTitle) {
      case "Visitor's Form":
        return <VisitorDetailsPage />;
      case "Application Form For Science Fair":
        return <ScienceDayDetails />;
      case "Exhibition Form":
        return <ExhibitionDetails />;
      case "Feedback Form":
        return <FeedbackDetails />;
      default:
        return <div>Unknown form type: {formTitle}</div>;
    }
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return (
      <div>
        <h1>Error</h1>
        <p>{error}</p>
        <button onClick={() => navigate('/')}>Go back to Dashboard</button>
      </div>
    );
  }

  return (
    <div>
      <h1>{formTitle || 'Form Details'}</h1>
      {renderFormContent()}
      <button onClick={() => navigate('/')}>Back to Dashboard</button>
    </div>
  );
};

export default FormDetailsPage;
