import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useSpring, animated, config } from 'react-spring';
import Calendar from './Calendar';
import PublishModal from './PublishModal';
import { ChevronLeft, Send, Calendar as CalendarIcon, Users, Search, Filter, Download, Trash2 } from 'lucide-react';
import visitorDetails from './visitorDetailspage';
import exhibitionDetails from './exhibitiondetailpage';
import feedbackDetails from './feedbackdetailspage';
import sciencedayDetails from './sciencedaydetails';

const FormDetailsPage = () => {
  const navigate = useNavigate();
  const { formId } = useParams();
  const [formType, setFormType] = useState('');

  useEffect(() => {
    // Fetch the form type based on the formId
    const fetchFormType = async () => {
      try {
        const response = await fetch(`http://localhost:5000/forms/${formId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch form data');
        }
        const data = await response.json();
        setFormType(data.type);
      } catch (error) {
        console.error('Error fetching form data:', error);
      }
    };

    fetchFormType();
  }, [formId]);

  // ... (keep the rest of your existing state and functions)

  const renderFormContent = () => {
    switch (formType) {
      case 'visitor':
        return <visitorDetails />;
      case 'exhibition':
        return <exhibitionDetails />;
      case 'feedback':
        return <feedbackDetails />;
       case 'scienceday':
        return <sciencedayDetails />;
      // Add cases for other form types
      default:
        return <div>Unknown form type</div>;
    }
  };

  return (
    <animated.div style={fadeIn} className="min-h-screen bg-gradient-to-br from-blue-100 to-blue-300 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <animated.div style={slideIn} className="bg-white shadow-2xl rounded-lg overflow-hidden">
          <div className="p-6 sm:p-10">
            <div className="flex justify-between items-center mb-8">
              <button
                className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={() => navigate(-1)}
              >
                <ChevronLeft className="mr-2" size={20} />
                Back
              </button>
              <button
                className="flex items-center px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={handlePublish}
              >
                <Send className="mr-2" size={20} />
                Publish
              </button>
            </div>

            <h1 className="text-4xl font-extrabold text-gray-900 mb-4">{formType.charAt(0).toUpperCase() + formType.slice(1)} Form</h1>
            
            {renderFormContent()}
          </div>
        </animated.div>
      </div>

      <PublishModal isOpen={isPublishModalOpen} setIsOpen={setIsPublishModalOpen} link={publishedLink} />
    </animated.div>
  );
};

export default FormDetailsPage;
