import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSpring, animated, config } from 'react-spring';
import Calendar from './Calendar';
import PublishModal from './PublishModal';
import { ChevronLeft, Send, Calendar as CalendarIcon, Users, Search, Filter, Download, Trash2 } from 'lucide-react';

const FormDetailsPage = () => {
  const navigate = useNavigate();

  const [selectedDates, setSelectedDates] = useState([]);
  const [isPublishModalOpen, setIsPublishModalOpen] = useState(false);
  const [publishedLink, setPublishedLink] = useState('');
  const [combinedData, setCombinedData] = useState([]); // New state for combined data
  const [fetchError, setFetchError] = useState(null);
  const [activeTab, setActiveTab] = useState('calendar');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItems, setSelectedItems] = useState([]);
  const [groupInchargeData, setGroupInchargeData] = useState([]);
  const [groupIncharge, setGroupIncharge] = useState([]);
  const [selectedGroupIncharge, setSelectedGroupIncharge] = useState([]);
  const [visitorArrivalData, setVisitorArrivalData] = useState([]);
  const [filteredVisitorArrivalData, setFilteredVisitorArrivalData] = useState([]);
  const [selectedVisitorArrival, setSelectedVisitorArrival] = useState([]);

  // Add the animations back
  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: config.molasses,
  });

  const slideIn = useSpring({
    transform: 'translateY(0)',
    from: { transform: 'translateY(50px)' },
    config: config.wobbly,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5000/visitors');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        
        if (data && Array.isArray(data.visitors) && Array.isArray(data.faculties)&& Array.isArray(data.groupincharge)&& Array.isArray(data.visitorArrival)) {
          // Combine visitors and faculties data
          const combined = data.visitors.map((visitor, index) => ({
            ...visitor,
            num_students: data.faculties[index]?.num_students || 'N/A',
            num_faculty: data.faculties[index]?.num_faculty || 'N/A'
          }));
          setCombinedData(combined);
          setGroupIncharge(data.groupincharge);
          setVisitorArrivalData(data.visitorArrival.map(item => ({
            ...item,
            campus: item.campus || 'N/A',
            ipr_time: item.ipr_time || 'N/A',
            fcipt_time: item.fcipt_time || 'N/A',
            visit_date: item.visit_date || 'N/A',
            visit_time: item.visit_time || 'N/A',
            materials: item.materials || 'N/A'
          })));
        } else {
          setFetchError('Invalid data structure');
        }
      } catch (err) {
        console.error(err);
        setFetchError(err.message);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const filtered = visitorArrivalData.filter(item =>
      item.campus.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.ipr_time.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.fcipt_time.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.visit_date.includes(searchTerm) ||
      item.visit_time.includes(searchTerm) ||
      item.materials.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredVisitorArrivalData(filtered);
  }, [visitorArrivalData, searchTerm]);

  const handleVisitorArrivalSelection = (id) => {
    setSelectedVisitorArrival(prev => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelectedVisitorArrival = () => {
    setVisitorArrivalData(prev => prev.filter(item => !selectedVisitorArrival.includes(item.id)));
    setSelectedVisitorArrival([]);
  };

  const filteredGroupInchargeData = groupInchargeData.filter(incharge =>
    incharge.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    incharge.position.toLowerCase().includes(searchTerm.toLowerCase()) ||
    incharge.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleGroupInchargeSelection = (id) => {
    setSelectedGroupIncharge(prev => 
      prev.includes(id) ? prev.filter(gId => gId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelectedGroupIncharge = () => {
    setGroupInchargeData(prev => prev.filter(incharge => !selectedGroupIncharge.includes(incharge.id)));
    setSelectedGroupIncharge([]);
  };

  const handleSelectDates = (dates) => {
    setSelectedDates(dates);
  };

  const handlePublish = () => {
    const link = `https://kritikaat.github.io/IPRProject/`;
    setPublishedLink(link);
    setIsPublishModalOpen(true);
  };

  const formatDisplayDate = (dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, parseInt(day) + 1);
    return date.toLocaleDateString();
  };

  const filteredData = combinedData.filter(item =>
    item.institution_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.student_branch.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.num_students.toString().includes(searchTerm) ||
    item.num_faculty.toString().includes(searchTerm)
  );

  const handleItemSelection = (id) => {
    setSelectedItems(prev => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelected = () => {
    setCombinedData(prev => prev.filter(item => !selectedItems.includes(item.id)));
    setSelectedItems([]);
  };

  return (
    <animated.div style={fadeIn} className="min-h-screen bg-gradient-to-br from-blue-100 to-blue-300 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <animated.div style={slideIn} className="bg-white shadow-2xl rounded-lg overflow-hidden">
          <div className="p-6 sm:p-10">
            <div className="flex justify-between items-center mb-8">
              <button
                className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={() => navigate(-1)}
              >
                <ChevronLeft className="mr-2" size={20} />
                Back
              </button>
              <button
                className="flex items-center px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={handlePublish}
              >
                <Send className="mr-2" size={20} />
                Publish
              </button>
            </div>

            <h1 className="text-4xl font-extrabold text-gray-900 mb-4">Visitor's Form</h1>
            <p className="text-gray-600 mb-8 text-lg">Manage visitor information and schedule.</p>

            <div className="mb-8 flex space-x-4">
              <button
                className={`flex-1 py-2 px-4 rounded-md transition duration-200 ease-in-out ${activeTab === 'calendar' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                onClick={() => setActiveTab('calendar')}
              >
                <CalendarIcon className="inline-block mr-2" size={20} />
                Calendar
              </button>
              <button
                className={`flex-1 py-2 px-4 rounded-md transition duration-200 ease-in-out ${activeTab === 'visitors' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                onClick={() => setActiveTab('visitors')}
              >
                <Users className="inline-block mr-2" size={20} />
                Visitor Data
              </button>
            </div>

            {activeTab === 'calendar' && (
              <div className="mb-12 bg-white p-6 rounded-lg shadow-md">
                <Calendar
                  onSelectDates={handleSelectDates}
                  selectedDates={selectedDates}
                />

                {selectedDates.length > 0 && (
                  <div className="mt-6 p-4 bg-blue-50 rounded-md border border-blue-200">
                    <h3 className="text-lg font-semibold text-blue-800 mb-2">Selected Dates:</h3>
                    <ul className="space-y-1">
                      {selectedDates.map((date, index) => (
                        <li key={index} className="text-blue-700">{formatDisplayDate(date)}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'visitors' && (
              <div className="mb-12">
                <div className="mb-4 flex space-x-4">
                  <div className="flex-grow flex items-center bg-gray-100 p-3 rounded-lg">
                    <Search size={20} className="mr-2 text-gray-400" />
                    <input
                      type="text"
                      placeholder="Search by name, institution, number of students, etc."
                      className="w-full bg-transparent focus:outline-none"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                  </div>
                  <button className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition">Filter <Filter size={16} className="inline ml-2" /></button>
                </div>

                {filteredData.length > 0 ? (
                  <table className="min-w-full bg-white border border-gray-200 rounded-lg">
                    <thead>
                      <tr>
                        <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700 bg-gray-100">Institution Name</th>
                        <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700 bg-gray-100">Student Branch</th>
                        <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700 bg-gray-100">Number of Students</th>
                        <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700 bg-gray-100">Number of Faculty</th>
                        <th className="px-4 py-2 text-center text-sm font-semibold text-gray-700 bg-gray-100">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredData.map((item) => (
                        <tr key={item.id} className="border-b border-gray-200">
                          <td className="px-4 py-2">{item.institution_name}</td>
                          <td className="px-4 py-2">{item.student_branch}</td>
                          <td className="px-4 py-2">{item.num_students}</td>
                          <td className="px-4 py-2">{item.num_faculty}</td>
                          <td className="px-4 py-2 text-center">
                            <button
                              onClick={() => handleItemSelection(item.id)}
                              className={`px-3 py-1 text-sm font-semibold rounded-md ${
                                selectedItems.includes(item.id) ? 'bg-red-500 text-white' : 'bg-gray-100 hover:bg-gray-200'
                              }`}
                            >
                              {selectedItems.includes(item.id) ? 'Deselect' : 'Select'}
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <p className="text-gray-600">No visitors found matching your search.</p>
                )}

                {selectedItems.length > 0 && (
                  <div className="mt-4 flex space-x-4">
                    <button
                      onClick={handleDeleteSelected}
                      className="flex items-center bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition duration-300"
                    >
                      <Trash2 size={16} className="mr-2" />
                      Delete Selected
                    </button>
                    <button className="flex items-center bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-300">
                      <Download size={16} className="mr-2" />
                      Export Selected
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>
        </animated.div>
      </div>

      {isPublishModalOpen && (
        <PublishModal
          isOpen={isPublishModalOpen}
          onClose={() => setIsPublishModalOpen(false)}
          publishedLink={publishedLink}
        />
      )}
    </animated.div>
  );
};

export default FormDetailsPage;
