import React, { useState, useEffect } from 'react';
import FormCard from './Formcard';
import SearchBar from './SearchBar';

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [activeTab, setActiveTab] = useState('Forms');

  useEffect(() => {
    setFilteredForms(forms);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  const tabs = ['Forms', 'Analytics'];

  const renderContent = () => {
    switch (activeTab) {
      case 'Forms':
        return (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredForms.map((form) => (
              <FormCard key={form.id} form={form} />
            ))}
          </div>
        );
      case 'Analytics':
        return <h2 className="text-2xl font-bold">Analytics Coming Soon</h2>;
      default:
        return null;
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <h1 className="text-2xl font-bold text-gray-900">Form Manager</h1>
            <div className="flex items-center space-x-4">
              <button className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded transition duration-300 ease-in-out">
                New Form
              </button>
              <div className="relative">
                <img 
                  src="/api/placeholder/32/32" 
                  alt="User" 
                  className="w-8 h-8 rounded-full cursor-pointer"
                />
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="flex flex-1 overflow-hidden">
        <nav className="w-64 bg-blue-800 text-white p-6">
          <ul className="space-y-2">
            {tabs.map((tab) => (
              <li 
                key={tab} 
                className={`py-2 px-4 rounded cursor-pointer transition-colors duration-200 ${
                  activeTab === tab ? 'bg-blue-600' : 'hover:bg-blue-700'
                }`}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
              </li>
            ))}
          </ul>
        </nav>

        <main className="flex-1 p-8 overflow-auto">
          <div className="mb-8 max-w-2xl">
            <SearchBar onSearch={handleSearch} className="w-full" />
          </div>

          {renderContent()}
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
