import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSpring, animated, config } from 'react-spring';
import Calendar from './Calendar';
import PublishModal from './PublishModal';
import { ChevronLeft, Send, Calendar as CalendarIcon, Users, Search, Filter, Download, Trash2 } from 'lucide-react';

const BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000'; // Configurable base URL

const FormDetailsPage = () => {
  const navigate = useNavigate();

  const [selectedDates, setSelectedDates] = useState([]);
  const [isPublishModalOpen, setIsPublishModalOpen] = useState(false);
  const [publishedLink, setPublishedLink] = useState('');
  const [visitorData, setVisitorData] = useState([]);
  const [facultyData, setFacultyData] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [loading, setLoading] = useState(true); // Loading state
  const [activeTab, setActiveTab] = useState('calendar');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedVisitors, setSelectedVisitors] = useState([]);
  
  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: config.molasses,
  });

  const slideIn = useSpring({
    transform: 'translateY(0)',
    from: { transform: 'translateY(50px)' },
    config: config.wobbly,
  });

  useEffect(() => {
    const fetchVisitorData = async () => {
      setLoading(true); // Set loading to true
      try {
        const response = await fetch(`${BASE_URL}/visitors`);
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        
        // Check if data has visitors and faculties arrays
        if (data && Array.isArray(data.visitors) && Array.isArray(data.faculties)) {
          setVisitorData(data.visitors);
          setFacultyData(data.faculties);
        } else {
          setFetchError('Invalid data structure');
        }
      } catch (err) {
        console.error(err);
        setFetchError(err.message);
      } finally {
        setLoading(false); // Set loading to false
      }
    };
    fetchVisitorData();
  }, []);

  const handleSelectDates = (dates) => {
    setSelectedDates(dates);
  };

  const handlePublish = () => {
    const link = `https://kritikaat.github.io/IPRProject/`;
    setPublishedLink(link);
    setIsPublishModalOpen(true);
  };

  const formatDisplayDate = (dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, parseInt(day) + 1);
    return date.toLocaleDateString();
  };

  const filteredVisitorData = visitorData.filter(visitor =>
    visitor.institution_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    visitor.student_branch.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleVisitorSelection = (id) => {
    setSelectedVisitors(prev => 
      prev.includes(id) ? prev.filter(vId => vId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelected = async () => {
    if (window.confirm("Are you sure you want to delete selected visitors?")) {
      const idsToDelete = selectedVisitors.join(',');
      try {
        const response = await fetch(`${BASE_URL}/visitors/${idsToDelete}`, {
          method: 'DELETE',
        });
        if (!response.ok) {
          throw new Error('Failed to delete selected visitors');
        }
        setVisitorData(prev => prev.filter(visitor => !selectedVisitors.includes(visitor.id)));
        setSelectedVisitors([]);
      } catch (err) {
        console.error(err);
        setFetchError(err.message);
      }
    }
  };

  return (
    <animated.div style={fadeIn} className="min-h-screen bg-gradient-to-br from-blue-100 to-blue-300 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <animated.div style={slideIn} className="bg-white shadow-2xl rounded-lg overflow-hidden">
          <div className="p-6 sm:p-10">
            <div className="flex justify-between items-center mb-8">
              <button
                className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={() => navigate(-1)}
              >
                <ChevronLeft className="mr-2" size={20} />
                Back
              </button>
              <button
                className="flex items-center px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
                onClick={handlePublish}
              >
                <Send className="mr-2" size={20} />
                Publish
              </button>
            </div>

            <h1 className="text-4xl font-extrabold text-gray-900 mb-4">Visitor's Form</h1>
            <p className="text-gray-600 mb-8 text-lg">Manage visitor information and schedule.</p>

            <div className="mb-8 flex space-x-4">
              <button
                className={`flex-1 py-2 px-4 rounded-md transition duration-200 ease-in-out ${activeTab === 'calendar' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                onClick={() => setActiveTab('calendar')}
              >
                <CalendarIcon className="inline-block mr-2" size={20} />
                Calendar
              </button>
              <button
                className={`flex-1 py-2 px-4 rounded-md transition duration-200 ease-in-out ${activeTab === 'visitors' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                onClick={() => setActiveTab('visitors')}
              >
                <Users className="inline-block mr-2" size={20} />
                Visitor Data
              </button>
            </div>

            {activeTab === 'calendar' && (
              <div className="mb-12 bg-white p-6 rounded-lg shadow-md">
                <Calendar
                  onSelectDates={handleSelectDates}
                  selectedDates={selectedDates}
                />

                {selectedDates.length > 0 && (
                  <div className="mt-6 p-4 bg-blue-50 rounded-md border border-blue-200">
                    <h3 className="text-lg font-semibold text-blue-800 mb-2">Selected Dates:</h3>
                    <ul className="space-y-1">
                      {selectedDates.map((date) => (
                        <li key={date} className="flex items-center text-blue-700">
                          <CalendarIcon className="mr-2" size={16} />
                          {formatDisplayDate(date)}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'visitors' && (
              <div className="mb-12">
                <h2 className="text-2xl font-bold text-gray-900 mb-6">Visitor Data</h2>
                
                <div className="mb-4 flex space-x-2">
                  <div className="flex-1 relative">
                    <input
                      type="text"
                      placeholder="Search visitors..."
                      className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
                  </div>
                  <button className="px-4 py-2 bg-blue-100 text-blue-700 rounded-md hover:bg-blue-200 transition duration-300 ease-in-out">
                    <Filter size={20} />
                  </button>
                  <button className="px-4 py-2 bg-green-100 text-green-700 rounded-md hover:bg-green-200 transition duration-300 ease-in-out">
                    <Download size={20} />
                  </button>
                  {selectedVisitors.length > 0 && (
                    <button 
                      className="px-4 py-2 bg-red-100 text-red-700 rounded-md hover:bg-red-200 transition duration-300 ease-in-out"
                      onClick={handleDeleteSelected}
                    >
                      <Trash2 size={20} />
                    </button>
                  )}
                </div>

                {loading ? (
                  <div>Loading visitors...</div>
                ) : fetchError ? (
                  <div className="text-red-500">{fetchError}</div>
                ) : (
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                      <tr>
                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          <input type="checkbox" onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedVisitors(visitorData.map(visitor => visitor.id));
                            } else {
                              setSelectedVisitors([]);
                            }
                          }} />
                        </th>
                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institution Name</th>
                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Branch</th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {filteredVisitorData.map((visitor) => (
                        <tr key={visitor.id} className={`hover:bg-gray-100 ${selectedVisitors.includes(visitor.id) ? 'bg-blue-50' : ''}`}>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <input type="checkbox" 
                              checked={selectedVisitors.includes(visitor.id)}
                              onChange={() => handleVisitorSelection(visitor.id)} />
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{visitor.institution_name}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{visitor.student_branch}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                )}
              </div>
            )}

            {isPublishModalOpen && <PublishModal link={publishedLink} onClose={() => setIsPublishModalOpen(false)} />}
          </div>
        </animated.div>
      </div>
    </animated.div>
  );
};

export default FormDetailsPage;
