import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Send, Filter, Download, Trash2, Search } from 'lucide-react';
import { exportToExcel, exportToCSV } from './exportUtils'; 

const FeedbackDetailsPage = () => {
  const navigate = useNavigate();
  const [feedbacks, setFeedbacks] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFeedbacks, setSelectedFeedbacks] = useState([]);
  const [selectedRatings, setSelectedRatings] = useState([]);
  const [activeTab, setActiveTab] = useState('feedback');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [feedbackResponse, ratingsResponse] = await Promise.all([
          axios.get(`http://localhost:3000/feedback`),
          axios.get(`http://localhost:3000/ratings`)
        ]);
        setFeedbacks(feedbackResponse.data);
        setRatings(ratingsResponse.data);
      } catch (error) {
        setError(`Error fetching data: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString();
  };

  const handleFeedbackSelection = (id) => {
    setSelectedFeedbacks(prev => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleRatingSelection = (id) => {
    setSelectedRatings(prev => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelectedFeedbacks = () => {
    setFeedbacks(prev => prev.filter(item => !selectedFeedbacks.includes(item.id)));
    setSelectedFeedbacks([]);
  };

  const handleDeleteSelectedRatings = () => {
    setRatings(prev => prev.filter(item => !selectedRatings.includes(item.id)));
    setSelectedRatings([]);
  };

  const handleExportFeedbacks = (format) => {
    const data = feedbacks.map(f => ({
      Institution: f.institutionName,
      Website: f.website,
      VisitDate: f.visitDate,
      StaffName: f.staffName,
      Email: f.staffEmail,
      Mobile: f.staffMobile,
      Students: f.totalStudents,
      AccompanyingStaff: f.accompanyingStaff,
      Source: f.sources,
      Campuses: f.campuses,
      Best: f.best,
      Worst: f.worst,
      Suggestions: f.suggestions,
      Comments: f.comments
    }));

    if (format === 'excel') {
      exportToExcel(data, 'feedbacks');
    } else {
      exportToCSV(data, 'feedbacks');
    }
  };

  const handleExportRatings = (format) => {
    const data = ratings.map(r => ({
      IPRRating: r.iprRating,
      FCIPTRating: r.fciptRating,
      Knowledge: r.knowledge,
      IPRExplanations: r.explanationsIPR,
      FCIPTExplanations: r.explanationsFCIPT,
      KnowledgeBefore: r.knowledgeBefore,
      KnowledgeAfter: r.knowledgeAfter,
      TechnicalContents: r.technicalContents,
      EaseOfUnderstanding: r.easeOfUnderstanding
    }));

    if (format === 'excel') {
      exportToExcel(data, 'ratings');
    } else {
      exportToCSV(data, 'ratings');
    }
  };

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );

  if (error) return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative">
        {error}
      </div>
    </div>
  );

  const TableHeader = ({ headers, selectedItems, items, onSelectAll }) => (
    <thead className="bg-gray-50">
      <tr>
        <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
          <input
            type="checkbox"
            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
            checked={selectedItems.length === items.length}
            onChange={onSelectAll}
          />
        </th>
        {headers.map(header => (
          <th key={header} className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
            {header}
          </th>
        ))}
      </tr>
    </thead>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8 flex justify-between items-center">
        <button
          onClick={() => navigate('/')}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg flex items-center"
        >
          Back to Dashboard
        </button>
      </div>

      <h1 className="text-4xl font-bold text-gray-800 mb-6">Feedback and Ratings</h1>
      <p className="text-gray-600 mb-8 text-lg">Manage feedback and rating information.</p>

      {/* Custom Tab Navigation */}
      <div className="mb-6">
        <div className="border-b border-gray-200">
          <div className="flex space-x-8">
            <button
              onClick={() => setActiveTab('feedback')}
              className={`py-4 px-1 relative ${
                activeTab === 'feedback'
                  ? 'text-blue-600 border-b-2 border-blue-600 font-medium'
                  : 'text-gray-500 hover:text-gray-700'
              } transition-colors duration-200`}
            >
              Feedback
              <span className={`absolute bottom-0 left-0 w-full h-0.5 transform transition-transform duration-200 ${
                activeTab === 'feedback' ? 'scale-x-100' : 'scale-x-0'
              } bg-blue-600`}></span>
            </button>
            <button
              onClick={() => setActiveTab('ratings')}
              className={`py-4 px-1 relative ${
                activeTab === 'ratings'
                  ? 'text-blue-600 border-b-2 border-blue-600 font-medium'
                  : 'text-gray-500 hover:text-gray-700'
              } transition-colors duration-200`}
            >
              Ratings
              <span className={`absolute bottom-0 left-0 w-full h-0.5 transform transition-transform duration-200 ${
                activeTab === 'ratings' ? 'scale-x-100' : 'scale-x-0'
              } bg-blue-600`}></span>
            </button>
          </div>
        </div>
      </div>

      {/* Tab Content */}
      <div className="relative">
        <div className={`transition-opacity duration-300 ${activeTab === 'feedback' ? 'opacity-100' : 'opacity-0 absolute top-0 left-0 w-full'}`}>
          {activeTab === 'feedback' && (
            <div className="space-y-6">
              <div className="flex justify-between items-center mb-6">
                <div className="flex space-x-2">
                  <div className="relative w-full">
                    <input
                      type="text"
                      placeholder="Search feedback..."
                      className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
                  </div>
                  <button className="p-2 bg-blue-100 text-blue-700 rounded-md hover:bg-blue-200 transition duration-300">
                    <Filter size={20} />
                  </button>
                </div>
                <div>
                  <button
                    onClick={() => handleExportFeedbacks('excel')}
                    className="flex items-center bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out"
                  >
                    Export to Excel
                    <Download className="ml-2" size={20} />
                  </button>
                  <button
                    onClick={() => handleExportFeedbacks('csv')}
                    className="ml-2 flex items-center bg-yellow-500 hover:bg-yellow-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out"
                  >
                    Export to CSV
                    <Download className="ml-2" size={20} />
                  </button>
                  <button
                    onClick={handleDeleteSelectedFeedbacks}
                    className={`ml-2 flex items-center ${selectedFeedbacks.length ? 'bg-red-500 hover:bg-red-600' : 'bg-gray-300 cursor-not-allowed'} text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out`}
                    disabled={!selectedFeedbacks.length}
                  >
                    Delete Selected
                    <Trash2 className="ml-2" size={20} />
                  </button>
                </div>
              </div>

              <table className="min-w-full bg-white border border-gray-200">
                <TableHeader
                  headers={['Institution', 'Website', 'Visit Date', 'Staff Name', 'Email', 'Mobile', 'Actions']}
                  selectedItems={selectedFeedbacks}
                  items={feedbacks}
                  onSelectAll={() => {
                    const allIds = selectedFeedbacks.length === feedbacks.length ? [] : feedbacks.map(f => f.id);
                    setSelectedFeedbacks(allIds);
                  }}
                />
                <tbody>
                  {feedbacks
                    .filter(f => f.institutionName.toLowerCase().includes(searchTerm.toLowerCase()))
                    .map((feedback) => (
                      <tr key={feedback.id} className={`hover:bg-gray-100 transition duration-200 ${selectedFeedbacks.includes(feedback.id) ? 'bg-blue-50' : ''}`}>
                        <td className="px-4 py-2 border-b border-gray-200">
                          <input
                            type="checkbox"
                            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                            checked={selectedFeedbacks.includes(feedback.id)}
                            onChange={() => handleFeedbackSelection(feedback.id)}
                          />
                        </td>
                        <td className="px-4 py-2 border-b border-gray-200">{feedback.institutionName}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{feedback.website}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{formatDate(feedback.visitDate)}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{feedback.staffName}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{feedback.staffEmail}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{feedback.staffMobile}</td>
                        <td className="px-4 py-2 border-b border-gray-200">
                          <button className="text-blue-600 hover:underline">Edit</button>
                          <button className="text-red-600 hover:underline ml-2" onClick={() => handleDeleteSelectedFeedbacks(feedback.id)}>Delete</button>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        <div className={`transition-opacity duration-300 ${activeTab === 'ratings' ? 'opacity-100' : 'opacity-0 absolute top-0 left-0 w-full'}`}>
          {activeTab === 'ratings' && (
            <div className="space-y-6">
              <div className="flex justify-between items-center mb-6">
                <div className="flex space-x-2">
                  <div className="relative w-full">
                    <input
                      type="text"
                      placeholder="Search ratings..."
                      className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
                  </div>
                  <button className="p-2 bg-blue-100 text-blue-700 rounded-md hover:bg-blue-200 transition duration-300">
                    <Filter size={20} />
                  </button>
                </div>
                <div>
                  <button
                    onClick={() => handleExportRatings('excel')}
                    className="flex items-center bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out"
                  >
                    Export to Excel
                    <Download className="ml-2" size={20} />
                  </button>
                  <button
                    onClick={() => handleExportRatings('csv')}
                    className="ml-2 flex items-center bg-yellow-500 hover:bg-yellow-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out"
                  >
                    Export to CSV
                    <Download className="ml-2" size={20} />
                  </button>
                  <button
                    onClick={handleDeleteSelectedRatings}
                    className={`ml-2 flex items-center ${selectedRatings.length ? 'bg-red-500 hover:bg-red-600' : 'bg-gray-300 cursor-not-allowed'} text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out`}
                    disabled={!selectedRatings.length}
                  >
                    Delete Selected
                    <Trash2 className="ml-2" size={20} />
                  </button>
                </div>
              </div>

              <table className="min-w-full bg-white border border-gray-200">
                <TableHeader
                  headers={['IPR Rating', 'FCIPT Rating', 'Knowledge Before', 'Knowledge After', 'Actions']}
                  selectedItems={selectedRatings}
                  items={ratings}
                  onSelectAll={() => {
                    const allIds = selectedRatings.length === ratings.length ? [] : ratings.map(r => r.id);
                    setSelectedRatings(allIds);
                  }}
                />
                <tbody>
                  {ratings
                    .filter(r => r.iprRating.toString().includes(searchTerm))
                    .map((rating) => (
                      <tr key={rating.id} className={`hover:bg-gray-100 transition duration-200 ${selectedRatings.includes(rating.id) ? 'bg-blue-50' : ''}`}>
                        <td className="px-4 py-2 border-b border-gray-200">
                          <input
                            type="checkbox"
                            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                            checked={selectedRatings.includes(rating.id)}
                            onChange={() => handleRatingSelection(rating.id)}
                          />
                        </td>
                        <td className="px-4 py-2 border-b border-gray-200">{rating.iprRating}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{rating.fciptRating}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{rating.knowledgeBefore}</td>
                        <td className="px-4 py-2 border-b border-gray-200">{rating.knowledgeAfter}</td>
                        <td className="px-4 py-2 border-b border-gray-200">
                          <button className="text-blue-600 hover:underline">Edit</button>
                          <button className="text-red-600 hover:underline ml-2" onClick={() => handleDeleteSelectedRatings(rating.id)}>Delete</button>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default FeedbackDetailsPage;
