import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

const Analytics = ({ data }) => {
  const { visitors, faculties, groupincharge, visitorArrival } = data;

  const institutionData = {
    labels: [...new Set(visitors.map(v => v.institution_name))],
    datasets: [{
      label: 'Number of Visitors',
      data: [...new Set(visitors.map(v => v.institution_name))].map(inst => 
        visitors.filter(v => v.institution_name === inst).length
      ),
      backgroundColor: 'rgba(75, 192, 192, 0.6)',
    }]
  };

  const branchData = {
    labels: [...new Set(visitors.map(v => v.student_branch))],
    datasets: [{
      label: 'Number of Students',
      data: [...new Set(visitors.map(v => v.student_branch))].map(branch => 
        visitors.filter(v => v.student_branch === branch).length
      ),
      backgroundColor: 'rgba(153, 102, 255, 0.6)',
    }]
  };

  const facultyStudentRatio = {
    labels: faculties.map((_, index) => `Visit ${index + 1}`),
    datasets: [
      {
        label: 'Students',
        data: faculties.map(f => f.num_students),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
      {
        label: 'Faculty',
        data: faculties.map(f => f.num_faculty),
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      }
    ]
  };

  const campusVisitData = {
    labels: ['IPR Main', 'FCIPT', 'Labs'],
    datasets: [{
      data: [
        visitorArrival.filter(v => v.campus.includes('ipr_main')).length,
        visitorArrival.filter(v => v.campus.includes('fcipt')).length,
        visitorArrival.filter(v => v.campus.includes('labs')).length,
      ],
      backgroundColor: ['rgba(255, 99, 132, 0.6)', 'rgba(54, 162, 235, 0.6)', 'rgba(255, 206, 86, 0.6)'],
    }]
  };

  return (
    <div>
      <h2>Visitor Analytics Dashboard</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Visitors by Institution</h3>
          <Bar data={institutionData} options={{ responsive: true }} />
        </div>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Students by Branch</h3>
          <Bar data={branchData} options={{ responsive: true }} />
        </div>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Faculty to Student Ratio</h3>
          <Bar data={facultyStudentRatio} options={{ responsive: true }} />
        </div>
        <div style={{ width: '45%', margin: '10px' }}>
          <h3>Campus Visit Distribution</h3>
          <Pie data={campusVisitData} options={{ responsive: true }} />
        </div>
      </div>
      <div>
        <h3>Additional Statistics</h3>
        <p>Total Group In-Charge: {groupincharge.length}</p>
        <p>Total Visitor Arrivals: {visitorArrival.length}</p>
      </div>
    </div>
  );
};

export default Analytics;
