import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSpring, animated } from 'react-spring';
import { Send, Search, Filter, Download, Trash2 } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';

const FeedbackDetailsPage = () => {
  const navigate = useNavigate();

  const [feedbacks, setFeedbacks] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('feedbacks');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItems, setSelectedItems] = useState([]);
  const [isPublishModalOpen, setIsPublishModalOpen] = useState(false);
  const [publishedLink, setPublishedLink] = useState('');

  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
  });

  const slideIn = useSpring({
    transform: 'translateY(0)',
    from: { transform: 'translateY(50px)' },
  });

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [feedbackResponse, ratingsResponse] = await Promise.all([
          fetch('http://localhost:3000/feedback'),
          fetch('http://localhost:3000/ratings')
        ]);
        const feedbackData = await feedbackResponse.json();
        const ratingsData = await ratingsResponse.json();
        setFeedbacks(feedbackData);
        setRatings(ratingsData);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError(`Error fetching data: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handlePublish = () => {
    const link = `https://example.com/published-feedback`;
    setPublishedLink(link);
    setIsPublishModalOpen(true);
  };

  const handleItemSelection = (id) => {
    setSelectedItems(prev => 
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const handleDeleteSelected = () => {
    const newData = activeTab === 'feedbacks' 
      ? feedbacks.filter(item => !selectedItems.includes(item.id))
      : ratings.filter(item => !selectedItems.includes(item.id));
    
    activeTab === 'feedbacks' ? setFeedbacks(newData) : setRatings(newData);
    setSelectedItems([]);
  };

  const filteredData = activeTab === 'feedbacks'
    ? feedbacks.filter(item =>
        item.institutionName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.staffName.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : ratings.filter(item =>
        item.feedbackId.toString().includes(searchTerm)
      );

  if (loading) return <div className="text-center py-10">Loading...</div>;
  if (error) return <Alert variant="destructive"><AlertTitle>Error</AlertTitle><AlertDescription>{error}</AlertDescription></Alert>;

  return (
    <animated.div style={fadeIn} className="container mx-auto px-4 py-8">
      <animated.div style={slideIn}>
        <div className="mb-8 flex justify-between items-center">
          <button
            onClick={() => navigate('/')}
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg flex items-center"
          >
            Back to Dashboard
          </button>
          <button
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg flex items-center"
            onClick={handlePublish}
          >
            <Send className="mr-2" size={20} />
            Publish
          </button>
        </div>

        <h1 className="text-4xl font-bold text-gray-800 mb-6">Feedback Details</h1>
        <p className="text-gray-600 mb-8 text-lg">Manage feedback and ratings information.</p>

        <div className="mb-8 flex space-x-4">
          {['feedbacks', 'ratings'].map((tab) => (
            <button
              key={tab}
              className={`flex-1 py-2 px-4 rounded-md transition duration-200 ease-in-out ${
                activeTab === tab
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              onClick={() => setActiveTab(tab)}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          ))}
        </div>

        <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
          <div className="mb-4 flex space-x-2">
            <div className="flex-1 relative">
              <input
                type="text"
                placeholder={`Search ${activeTab}...`}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
            </div>
            <button className="px-4 py-2 bg-blue-100 text-blue-700 rounded-md hover:bg-blue-200 transition duration-300 ease-in-out">
              <Filter size={20} />
            </button>
            <button className="px-4 py-2 bg-green-100 text-green-700 rounded-md hover:bg-green-200 transition duration-300 ease-in-out">
              <Download size={20} />
            </button>
            {selectedItems.length > 0 && (
              <button
                className="px-4 py-2 bg-red-100 text-red-700 rounded-md hover:bg-red-200 transition duration-300 ease-in-out"
                onClick={handleDeleteSelected}
              >
                <Trash2 size={20} />
              </button>
            )}
          </div>

          {filteredData.length > 0 ? (
            <div className="overflow-x-auto bg-white rounded-lg shadow">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      <input
                        type="checkbox"
                        className="h-5 w-5 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                        checked={selectedItems.length === filteredData.length}
                        onChange={() => {
                          if (selectedItems.length === filteredData.length) {
                            setSelectedItems([]);
                          } else {
                            setSelectedItems(filteredData.map(item => item.id));
                          }
                        }}
                      />
                    </th>
                    {activeTab === 'feedbacks' ? (
                      <>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institution Name</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Staff Name</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Visit Date</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Total Students</th>
                      </>
                    ) : (
                      <>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Feedback ID</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">IPR Rating</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">FCIPT Rating</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Knowledge</th>
                      </>
                    )}
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredData.map(item => (
                    <tr key={item.id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <input
                          type="checkbox"
                          className="h-5 w-5 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                          checked={selectedItems.includes(item.id)}
                          onChange={() => handleItemSelection(item.id)}
                        />
                      </td>
                      {activeTab === 'feedbacks' ? (
                        <>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.institutionName}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.staffName}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{new Date(item.visitDate).toLocaleDateString()}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.totalStudents}</td>
                        </>
                      ) : (
                        <>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.feedbackId}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.iprRating}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.fciptRating}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.knowledge}</td>
                        </>
                      )}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-gray-600 p-4 rounded-md shadow">No data found.</div>
          )}
        </div>
      </animated.div>

      {/* You would need to implement a PublishModal component similar to the one in VisitorDetailsPage */}
      {/* <PublishModal isOpen={isPublishModalOpen} setIsOpen={setIsPublishModalOpen} link={publishedLink} /> */}
    </animated.div>
  );
};

export default FeedbackDetailsPage;
