import React, { useState, useEffect } from 'react';
import FormCard from './FormCard';
import SearchBar from './SearchBar';

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [activeTab, setActiveTab] = useState('Dashboard');

  useEffect(() => {
    setFilteredForms(forms);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  const tabs = ['Dashboard', 'Forms', 'Analytics', 'Settings'];

  const renderContent = () => {
    switch (activeTab) {
      case 'Forms':
        return (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredForms.map((form) => (
              <FormCard key={form.id} form={form} />
            ))}
          </div>
        );
      case 'Dashboard':
        return <h2 className="text-2xl font-bold">Welcome to your Dashboard</h2>;
      case 'Analytics':
        return <h2 className="text-2xl font-bold">Analytics Coming Soon</h2>;
      case 'Settings':
        return <h2 className="text-2xl font-bold">Settings Page</h2>;
      default:
        return null;
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <div className="w-64 bg-indigo-800 text-white p-6">
        <h2 className="text-2xl font-bold mb-6">Form Manager</h2>
        <ul className="space-y-2">
          {tabs.map((tab) => (
            <li 
              key={tab} 
              className={`py-2 px-4 rounded cursor-pointer transition-colors duration-200 ${
                activeTab === tab ? 'bg-indigo-600' : 'hover:bg-indigo-700'
              }`}
              onClick={() => setActiveTab(tab)}
            >
              {tab}
            </li>
          ))}
        </ul>
      </div>

      <div className="flex-1 p-8 overflow-auto">
        <div className="mb-8 max-w-2xl">
          <SearchBar onSearch={handleSearch} className="w-full" />
        </div>

        {renderContent()}
      </div>
    </div>
  );
};

export default Dashboard;
