import React, { useState, useEffect } from 'react';
import FormCard from './Formcard';
import SearchBar from './SearchBar';

const Dashboard = ({ forms }) => {
  const [filteredForms, setFilteredForms] = useState([]);

  useEffect(() => {
    // When forms are received, set them to filteredForms
    setFilteredForms(forms);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms); // Reset to all forms if query is empty
      return;
    }

    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  const SearchBar = ({ onSearch, className }) => {
    const [query, setQuery] = useState(''); // State to store the input value
  
    const handleInputChange = (e) => {
      setQuery(e.target.value); // Update the input value
      onSearch(e.target.value); // Trigger the search function
    };
  
    return (
      <input
        type="text"
        value={query} // Bind the value to the state
        onChange={handleInputChange} // Handle input changes
        placeholder="Search..."
        className={`block w-full p-3 border border-gray-300 rounded-lg shadow-sm ${className}`}
      />
    );
  };

  return (
    <div className="flex h-screen bg-gray-200">
      <div className="w-64 bg-gray-800 p-5 text-white">
        <h2 className="mb-5 text-lg">Form Manager</h2>
        <ul className="list-none p-0">
          <li className="py-2 cursor-pointer hover:bg-gray-700 pl-2">Dashboard</li>
          <li className="py-2 cursor-pointer hover:bg-gray-700 pl-2">Analytics</li>
          <li className="py-2 cursor-pointer hover:bg-gray-700 pl-2">Settings</li>
        </ul>
      </div>

      <div className="flex-1 p-6  text-gray-700">
        {/* Increase the width of the SearchBar */}
        <div className="mb-8">
  <SearchBar
    onSearch={handleSearch}
    className="w-full mx-auto p-3 border border-gray-300 rounded-lg shadow-sm"
  />
</div>


  {/* Increase the spacing between the FormCard components */}
  <div className="grid grid-cols-[repeat(auto-fill,minmax(250px,1fr))] gap-8">
    {filteredForms.length > 0 ? (
      filteredForms.map((form) => (
        <FormCard key={form.id} form={form} />
      ))
    ) : (
      <p>Loading Forms....</p>
    )}
  </div>
</div>

    </div>
  );
};

export default Dashboard;
