import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSpring, animated } from 'react-spring';
import Calendar from './Calendar';
import PublishModal from './PublishModal';

const FormDetailsPage = () => {
  const navigate = useNavigate();

  const [selectedDates, setSelectedDates] = useState([]);
  const [isPublishModalOpen, setIsPublishModalOpen] = useState(false);
  const [publishedLink, setPublishedLink] = useState('');
  const [visitorData, setVisitorData] = useState([]);
  const [fetchError, setFetchError] = useState(null);

  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: { duration: 1000 }
  });

  const slideIn = useSpring({
    transform: 'translateY(0)',
    from: { transform: 'translateY(50px)' },
    config: { mass: 1, tension: 120, friction: 14 }
  });

  useEffect(() => {
    const fetchVisitorData = async () => {
      try {
        const response = await fetch('http://localhost:5000/visitors');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setVisitorData(data);
      } catch (error) {
        console.error('Error fetching visitor data:', error);
        setFetchError('Error fetching visitor data');
      }
    };
    fetchVisitorData();
  }, []);

  const handleSelectDates = (dates) => {
    setSelectedDates(dates);
  };

  const handlePublish = () => {
    const link = `https://kritikaat.github.io/IPRProject/`;
    setPublishedLink(link);
    setIsPublishModalOpen(true);
  };

  const formatDisplayDate = (dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, parseInt(day) + 1);
    return date.toLocaleDateString();
  };

  return (
    <animated.div style={fadeIn} className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <animated.div style={slideIn} className="bg-white shadow-xl rounded-lg overflow-hidden">
          <div className="p-6 sm:p-10">
            <div className="flex justify-between items-center mb-6">
              <button
                className="px-4 py-2 bg-gray-800 text-white rounded-md hover:bg-gray-700 transition duration-300 ease-in-out transform hover:-translate-y-1"
                onClick={() => navigate(-1)}
              >
                Back
              </button>
              <button
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-500 transition duration-300 ease-in-out transform hover:-translate-y-1"
                onClick={handlePublish}
              >
                Publish
              </button>
            </div>

            <h1 className="text-3xl font-bold text-gray-900 mb-4">Visitor's Form</h1>
            <p className="text-gray-600 mb-8">Select dates and view visitor data below.</p>

            <div className="mb-12">
              <Calendar
                onSelectDates={handleSelectDates}
                selectedDates={selectedDates}
              />

              {selectedDates.length > 0 && (
                <div className="mt-6 p-4 bg-blue-50 rounded-md">
                  <h3 className="text-lg font-semibold text-blue-800 mb-2">Selected Dates:</h3>
                  <ul className="list-disc list-inside text-blue-700">
                    {selectedDates.map((date) => (
                      <li key={date} className="mb-1">
                        {formatDisplayDate(date)}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>

            <h2 className="text-2xl font-bold text-gray-900 mb-6">Visitor Data</h2>
            
            {fetchError ? (
              <div className="text-red-600 bg-red-100 p-4 rounded-md">{fetchError}</div>
            ) : (
              visitorData.length > 0 && (
                <div className="overflow-x-auto">
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institution Name</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Branch</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Semester</th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {visitorData.map((visitor) => (
                        <tr key={visitor.id} className="hover:bg-gray-50 transition duration-150 ease-in-out">
                          <td className="px-6 py-4 whitespace-nowrap">{visitor.institution_name}</td>
                          <td className="px-6 py-4 whitespace-nowrap">{visitor.student_branch}</td>
                          <td className="px-6 py-4 whitespace-nowrap">{visitor.student_sem}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )
            )}
          </div>
        </animated.div>
      </div>

      <PublishModal
        isOpen={isPublishModalOpen}
        onClose={() => setIsPublishModalOpen(false)}
        publishedLink={publishedLink}
      />
    </animated.div>
  );
};

export default FormDetailsPage;
