import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie, Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

const AnalyticsPage = () => {
  const [analytics, setAnalytics] = useState({
    totalForms: 0,
    materials: [],
    campusVisits: [],
    visitTimes: [],
    studentCount: 0,
    facultyCount: 0,
  });

  useEffect(() => {
    fetchAnalyticsData();
  }, []);

  const fetchAnalyticsData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/visitors');
      const { visitors, faculties, visitorArrival } = response.data;

      setAnalytics({
        totalForms: visitors.length,
        materials: processMaterials(visitorArrival),
        campusVisits: processCampusVisits(visitorArrival),
        visitTimes: processVisitTimes(visitorArrival),
        studentCount: visitors.filter(v => v.category === 'Student').length,
        facultyCount: faculties.length,
      });
    } catch (error) {
      console.error('Error fetching analytics data:', error);
    }
  };

  // ... (keep the processing functions for materials, campusVisits, and visitTimes)

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Analytics',
      },
    },
  };

  return (
    <div className="analytics-page bg-gray-100 p-6 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Visit Analytics</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* ... (keep the existing chart components for totalForms, materials, campusVisits, visitTimes, and visitorTypes) */}
      </div>
    </div>
  );
};

export default AnalyticsPage;
