import React, { useState, useReducer } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css"; //

// Define all steps with unique IDs for each field
const steps = [
    {
        title: "Hall for Exhibition",
        fields: [
            { id: 'hallDimension', label: 'Hall Dimension', required: true, type: "text", helperText: "Dimension of the hall (LxB in meters). As large as possible (Min 15mx15m). Size available: 15m X 10m" },
            { id: 'isEnclosed', label: "Is it an enclosed hall?", required: true, type: "text", helperText: "Enclosed hall required for safety" },
            { id: 'canBeDarkened', label: "Can the hall be darkened with curtains?", required: true, type: "text", helperText: "Hall should be dark to be able to see plasma properly" },
            { id: 'hasAcOrFans', label: "Does the hall have A/C or fans?", required: true, type: "text", helperText: "Cooling is required to keep working models cool" },
            { id: 'isGroundFloor', label: "Is it on the ground floor?", required: true, type: "text", helperText: "Preferably on ground floor for easy unloading" },
            { id: 'hasStorage', label: "Is there storage space for empty exhibit boxes?", required: true, type: "text", helperText: "Storage space is preferable for easy management" },
            { id: 'powerOutlets', label: "No. of 230V (5/15A) power outlets in the hall", required: true, type: "text", helperText: "Minimum 30 outlets needed" },
            { id: 'tables', label: "Tables for placing the exhibits", required: true, type: "text", helperText: "35 tables and 20 chairs needed" },
            { id: 'vrSpace', label: "Space for VR", required: true, type: "text", helperText: "Min 3m x 3m for one VR exhibit" },
            { id: 'wifiAccess', label: "WiFi Access", required: true, type: "text", helperText: "WiFi access needed for team members" }
        ]
    },
    {
        title: "Hall for Lectures/Quiz/Tokotoy Competition",
        fields: [
            { id: 'lectureHallArea', label: "Area of the hall", required: true, type: "text", helperText: "Should be able to comfortably seat the audience" },
            { id: 'seatingCapacity', label: "Seating capacity", required: true, type: "text", helperText: "Should be able to comfortably seat a minimum of 50 people" },
            { id: 'avFacilities', label: "Does the hall have A/V facilities?", required: true, type: "text", helperText: "Requires digital projector (HDMI input) and audio system" },
            { id: 'distanceFromExhibition', label: "Distance of lecture hall from exhibition hall", required: true, type: "text", helperText: "As close to the exhibition hall as possible" }
        ]
    },
    {
        title: "Logistics and Accommodation",
        fields: [
            { id: 'accommodation', label: "Accommodation for IPR team", required: true, type: "text", helperText: "8 rooms, A/C, single occupancy required" },
            { id: 'transportation', label: "Local transportation for IPR team", required: true, type: "text", helperText: "For minimum 8 people - airport/railway station pickup/drop" },
            { id: 'truckParking', label: "Secure parking space for IPR truck", required: true, type: "text", helperText: "Preferably within the campus" },
            { id: 'loadingManpower', label: "Manpower for loading/unloading", required: true, type: "text", helperText: "At least FOUR persons needed" }
        ]
    },
    {
        title: "Event Poster Details",
        fields: [
            { id: 'contactPerson', label: "Name of the contact person", required: true, type: "text", helperText: "Will appear on event poster" },
            { id: 'contactMobile', label: "Mobile Number", required: true, type: "text", helperText: "Will appear on event poster" },
            { id: 'contactEmail', label: "E-mail", required: true, type: "text", helperText: "Will appear on event poster" },
            { id: 'venueLocation', label: "Location of the venue", required: true, type: "text", helperText: "Full address including hall name, building, etc." }
        ]
    },
    {
        title: "Teacher Training Program",
        fields: [
            { id: 'teacherInvitation', label: "Invitation to teachers", required: true, type: "text", helperText: "Minimum 25, Maximum 50 teachers" },
            { id: 'teacherRegistration', label: "Registration", required: true, type: "text", helperText: "Minimum 25, Maximum 50" },
            { id: 'teacherMaterials', label: "Writing materials provision", required: true, type: "text", helperText: "Writing pad/pen etc. for participants" },
            { id: 'teacherRefreshments', label: "Refreshments arrangement", required: true, type: "text", helperText: "Tea/lunch arrangements for participants" }
        ]
    },
    {
        title: "Quiz Programme",
        fields: [
            { id: 'quizParticipants', label: "Quiz participation details", required: true, type: "text", helperText: "For school students (8-12 classes)" },
            { id: 'teamRegistration', label: "Team registration process", required: true, type: "text", helperText: "Minimum 10 teams, 2 participants per team" },
            { id: 'quizArrangements', label: "Quiz arrangements", required: true, type: "text", helperText: "Arrangements for organizing the quiz" },
            { id: 'quizRefreshments', label: "Refreshments for participants", required: true, type: "text", helperText: "Arrangements for participants" }
        ]
    }
];

const initialState = steps.reduce((acc, step) => {
    step.fields.forEach(field => {
        acc[field.id] = '';
    });
    return acc;
}, {});

const formReducer = (state, action) => {
    switch (action.type) {
        case 'SET_FIELD':
            return { ...state, [action.field]: action.value };
        case 'RESET_FORM':
            return initialState;
        default:
            return state;
    }
};

const FormField = ({ id, label, required, helperText, value, onChange, error }) => (
    <div className="mb-4">
        <label className="block font-semibold text-gray-700 mb-1">
            {label} {required && <span className="text-red-500">*</span>}
        </label>
        <input
            type="text"
            className={`w-full p-2 border rounded-lg ${error ? 'border-red-500' : 'border-gray-300'}`}
            required={required}
            value={value}
            onChange={onChange}
            placeholder={`Enter ${label.toLowerCase()}`}
        />
        {error && (
            <div className="mt-1 text-sm text-red-500">
                {error}
            </div>
        )}
        {helperText && <p className="text-sm text-gray-600 mt-1">{helperText}</p>}
    </div>
);

const IPRExForm = () => {
    const [formData, dispatch] = useReducer(formReducer, initialState);
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(0);
    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);

    const validateStep = () => {
        const currentFields = steps[currentStep].fields;
        const newErrors = {};
        let isValid = true;

        currentFields.forEach(field => {
            if (field.required && !formData[field.id]?.trim()) {
                newErrors[field.id] = `${field.label} is required`;
                isValid = false;
            }
        });

        setErrors(newErrors);
        return isValid;
    };

    const validateForm = () => {
        const allErrors = {};
        let isValid = true;

        steps.forEach(step => {
            step.fields.forEach(field => {
                if (field.required && !formData[field.id]?.trim()) {
                    allErrors[field.id] = `${field.label} is required`;
                    isValid = false;
                }
            });
        });

        setErrors(allErrors);
        return isValid;
    };

    const handleNext = () => {
        if (validateStep()) {
            setCurrentStep(prev => prev + 1);
            window.scrollTo(0, 0);
        } else {
            toast.error("Please fill in all required fields before proceeding");
        }
    };

    const handlePrevious = () => {
        setCurrentStep(prev => prev - 1);
        window.scrollTo(0, 0);
    };

    const handleInputChange = (fieldId, value) => {
        dispatch({ type: 'SET_FIELD', field: fieldId, value });
        if (errors[fieldId]) {
            setErrors(prev => ({ ...prev, [fieldId]: '' }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) {
            toast.error("Please fill in all required fields");
            return;
        }

        setIsSubmitting(true);

        try {
            // Transform the form data to match your backend's expected structure
            const transformedData = {
                // Hall for Exhibition
                hallDimension: formData.hallDimension,
                isEnclosedHall: formData.isEnclosed,
                canBeDarkened: formData.canBeDarkened,
                hasCooling: formData.hasAcOrFans,
                isGroundFloor: formData.isGroundFloor,
                hasStorageSpace: formData.hasStorage,
                powerOutlets: formData.powerOutlets,
                numTables: formData.tables,
                vrSpace: formData.vrSpace,
                hasWifi: formData.wifiAccess,

                // Hall for Lectures
                lectureHallArea: formData.lectureHallArea,
                seatingCapacity: formData.seatingCapacity,
                hasAVFacilities: formData.avFacilities,
                distanceFromExhibition: formData.distanceFromExhibition,

                // Logistics and Accommodation
                accommodationProvided: formData.accommodation,
                localTransportation: formData.transportation,
                secureParkingSpace: formData.truckParking,
                manpowerForLoading: formData.loadingManpower,

                // Event Poster Details
                contactPersonName: formData.contactPerson,
                contactPersonMobile: formData.contactMobile,
                contactPersonEmail: formData.contactEmail,
                venueLocation: formData.venueLocation,

                // Teacher Training Program
                teacherInvitation: formData.teacherInvitation,
                teacherRegistration: formData.teacherRegistration,
                providesWritingMaterials: formData.teacherMaterials,
                providesRefreshments: formData.teacherRefreshments,

                // Quiz Programme
                quizForSchoolStudents: formData.quizParticipants,
                quizTeamSelection: formData.teamRegistration,
                quizArrangements: formData.quizArrangements,
                quizRefreshments: formData.quizRefreshments
            };

            console.log('Submitting transformed data:', transformedData);

            const response = await fetch('http://localhost:3000/iprexhibition', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify(transformedData)
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || `Submission failed with status ${response.status}`);
            }

            toast.success("Form submitted successfully!");
            setTimeout(() => {
                dispatch({ type: 'RESET_FORM' });
                navigate('/success');
            }, 2000);

        } catch (error) {
            console.error('Submission error:', error);
            toast.error(error.message || "Error submitting form. Please try again.");
        } finally {
            setIsSubmitting(false);
        }
    };


    return (
        <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-lg">
            <ToastContainer />
            <div className="mb-8">
                <h2 className="text-2xl font-bold text-gray-800">{steps[currentStep].title}</h2>
                <div className="mt-2 flex gap-2">
                    {steps.map((_, index) => (
                        <div
                            key={index}
                            className={`h-2 flex-1 rounded-full ${
                                index <= currentStep ? 'bg-blue-500' : 'bg-gray-200'
                            }`}
                        />
                    ))}
                </div>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
                {steps[currentStep].fields.map((field) => (
                    <FormField
                        key={field.id}
                        id={field.id}
                        label={field.label}
                        required={field.required}
                        helperText={field.helperText}
                        value={formData[field.id]}
                        onChange={(e) => handleInputChange(field.id, e.target.value)}
                        error={errors[field.id]}
                    />
                ))}

                <div className="flex justify-between mt-6 pt-4 border-t border-gray-200">
                    {currentStep > 0 && (
                        <button
                            type="button"
                            onClick={handlePrevious}
                            className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
                        >
                            Previous
                        </button>
                    )}
                    {currentStep < steps.length - 1 ? (
                        <button
                            type="button"
                            onClick={handleNext}
                            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                        >
                            Next
                        </button>
                    ) : (
                        <button
                            type="submit"
                            className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors"
                        >
                            Submit
                        </button>
                    )}
                </div>
            </form>
        </div>
    );
};

export default IPRExForm;
