import React, { useState, useReducer } from 'react'; // If you are using it
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from "react-toastify";

// Define the steps with fields
const steps = [
    {
        title: "Hall for Exhibition",
        fields: [
            { label: 'Hall Dimension', required: true, type: "text", helperText: "Dimension of the hall (LxB in meters). As large as possible (Min 15mx15m). Size available: 15m X 10m" },
            { label: "Is it an enclosed hall?", required: true, type: "text", helperText: "Enclosed hall required for safety" },
            { label: "Can the hall be darkened with curtains?", required: true, type: "text", helperText: "Hall should be dark to be able to see plasma properly. Either dark curtains or black paper covering over windows should be done." },
            { label: "Does the hall have A/C or fans?", required: true, type: "text", helperText: "Cooling is required to keep working models cool" },
            { label: "Is it on the ground floor?", required: true, type: "text", helperText: "Preferably on ground floor so that exhibits can be unloaded and taken to hall from truck. If exhibition hall is on the upper floor, then additional labour will be required for quick transfer of exhibits to the hall." },
            { label: "Is there storage space for empty exhibit boxes?", required: true, type: "text", helperText: "Storage space is preferable for easy management." },
            { label: "No. of 230V (5/15A) power outlets in the hall", required: true, type: "text", helperText: "We will need minimum 30 no’s of 230V/5A, grounded power outlets." },
            { label: "Tables for placing the exhibits", required: true, type: "text", helperText: "Sturdy, flat-top tables with wooden top and dark cloth cover (typical size 1.0x 0.5m or bigger): 35 numbers and 20 chairs." },
            { label: "Space for VR", required: true, type: "text", helperText: "Min 3m x 3m for one VR exhibit. If more space is made available, up to 4 VR exhibits can be operated." },
            { label: "WiFi Access", required: true, type: "text", helperText: "WiFi access to all IPR team members for running VR exhibits and for performing office duties during the event." }
        ]
    },
    {
        title: "Hall for lectures/quiz/Tokotoy competition",
        fields: [
            { label: "Area of the hall", required: true, type: "text", helperText: "Should be able to comfortably seat the audience." },
            { label: "Seating capacity", required: true, type: "text", helperText: "Should be able to comfortably seat a minimum of 50 people, with tables for conducting training programs, quizzes, and Tokotoy competition." },
            { label: "Does the hall have A/V facilities?", required: true, type: "text", helperText: "Requires digital projector (HDMI input) and audio system." },
            { label: "Distance of lecture hall from exhibition hall", required: true, type: "text", helperText: "As close to the exhibition hall as possible." }
        ]
    },
    {
        title: "Logistics and Accommodation Requirements for the IPR Team",
        fields: [
            { label: "Accommodation for IPR team (8 rooms, A/C, single occupancy)", required: true, type: "text", helperText: "With the new government order stipulating that officers on duty should use government guest houses, host will have to either provide accommodation at their cost or ensure that Government guest house is made available." },
            { label: "Local transportation for IPR team (Min. 8 people)", required: true, type: "text", helperText: "Host will have to provide airport/railway station pickup/drop, daily transportation from accommodation to venue and back." },
            { label: "Secure parking space for IPR truck that will transport the exhibits", required: true, type: "text", helperText: "Preferably within the campus and also providing basic amenities to the truck driver." },
            { label: "Manpower for loading/unloading the exhibits from the truck and moving it to the exhibition hall", required: true, type: "text", helperText: "At least FOUR persons will be needed to help the driver to unload/load the exhibits from the truck and move it to the exhibition hall." }
        ]
    },
    {
        title: "Details for the Event Poster",
        fields: [
            { label: "Name of the contact person", required: true, type: "text", helperText: "These details will appear on the event poster that IPR will make and send it to the host for circulation." },
            { label: "Mobile Number", required: true, type: "text", helperText: "These details will appear on the event poster that IPR will make and send it to the host for circulation." },
            { label: "E-mail", required: true, type: "text", helperText: "These details will appear on the event poster that IPR will make and send it to the host for circulation." },
            { label: "Location of the venue", required: true, type: "text", helperText: "Full address of the venue of the exhibition (including name of the hall, building, etc.)." }
        ]
    },
    {
        title: "Teacher Training Program",
        fields: [
            { label: "Invitation to teachers", required: true, type: "text", helperText: "Minimum number – 25. Maximum number – 50." },
            { label: "Registration", required: true, type: "text", helperText: "Minimum number – 25. Maximum number – 50." },
            { label: "Providing them with writing pad/pen etc.", required: true, type: "text", helperText: "Minimum number – 25. Maximum number – 50." },
            { label: "Providing them with tea/lunch", required: true, type: "text", helperText: "Minimum number – 25. Maximum number – 50." }
        ]
    },
    {
        title: "Quiz Programme",
        fields: [
            { label: "Quiz is meant for school students (8-12 classes)", required: true, type: "text", helperText: "Minimum teams 10. No. of participants/team – 2." },
            { label: "Selection and registration of teams", required: true, type: "text", helperText: "Minimum teams 10. No. of participants/team – 2." },
            { label: "Arrangements for organizing the quiz", required: true, type: "text", helperText: "Minimum teams 10. No. of participants/team – 2." },
            { label: "Providing refreshment to participants", required: true, type: "text", helperText: "Minimum teams 10. No. of participants/team – 2." }
        ]
    }
];

const initialState = steps.reduce((acc, step) => {
    step.fields.forEach(field => {
        const key = field.label.replace(/\s+/g, '').toLowerCase(); // Key transformation
        acc[key] = ''; // Initialize all fields as empty strings
    });
    return acc;
}, {});

// Reducer function for managing form state
const formReducer = (state, action) => {
    switch (action.type) {
        case 'SET_FIELD':
            return { ...state, [action.field]: action.value };
        default:
            return state;
    }
};

// FormField component for rendering individual fields
const FormField = ({ label, required, helperText, value, onChange }) => (
    <div>
        <label className="block font-extrabold text-gray-700">
            {label} {required && <span className="text-red-500">*</span>}
        </label>
        <input
            type="text"
            className="w-full p-2 border border-gray-300 rounded-lg mt-1"
            required={required}
            value={value}
            placeholder={`Enter ${label.toLowerCase()}`}
            onChange={onChange}
        />
        {helperText && <p className="text-sm text-gray-600 mt-1 mb-4">{helperText}</p>}
    </div>
);

const IPRExForm = () => {
    const [formData, dispatch] = useReducer(formReducer, initialState);
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(0);

    const handleInputChange = (index, value) => {
        const fieldKey = steps[currentStep].fields[index].label.replace(/\s+/g, '').toLowerCase();
        dispatch({ type: 'SET_FIELD', field: fieldKey, value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Form Data:', formData); // Log the formData

        try {
            const response = await fetch('http://localhost:3000/iprexhibition', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                toast.success("Form submitted successfully!");
            } else {
                const errorText = await response.text(); // Get response body text
                console.error("Error details:", errorText);
                toast.error("There was an error submitting the form.");
            }
        } catch (error) {
            console.error("Error submitting form:", error);
            toast.error("There was an error submitting the form.");
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-4">
            <ToastContainer />
            <h2 className="text-2xl font-bold mb-6">{steps[currentStep].title}</h2>
            <form onSubmit={handleSubmit}>
                {steps[currentStep].fields.map((field, index) => (
                    <FormField
                        key={index}
                        label={field.label}
                        required={field.required}
                        helperText={field.helperText}
                        value={formData[field.label.replace(/\s+/g, '').toLowerCase()]}
                        onChange={(e) => handleInputChange(index, e.target.value)}
                    />
                ))}
                <div className="flex justify-between mt-4">
                    {currentStep > 0 && (
                        <button
                            type="button"
                            className="px-4 py-2 bg-gray-500 text-white rounded-lg"
                            onClick={() => setCurrentStep(prev => prev - 1)}
                        >
                            Previous
                        </button>
                    )}
                    {currentStep < steps.length - 1 ? (
                        <button
                            type="button"
                            className="px-4 py-2 bg-blue-500 text-white rounded-lg"
                            onClick={() => setCurrentStep(prev => prev + 1)}
                        >
                            Next
                        </button>
                    ) : (
                        <button
                            type="submit"
                            className="px-4 py-2 bg-green-500 text-white rounded-lg"
                        >
                            Submit
                        </button>
                    )}
                </div>
            </form>
        </div>
    );
};

export default IPRExForm;
