import React, { useState } from 'react';

const FeedbackForm = () => {
    const [formData, setFormData] = useState({
        institutionName: 'LDRP Institute of Technology and Research',
        website: '',
        visitDate: '',
        staffName: '',
        staffEmail: '',
        staffMobile: '',
        totalStudents: '',
        accompanyingStaff: '',
        sources: [],
        campuses: [],
        ratings: {
            iprRating: '',
            fciptRating: '',
            knowledge: '',
            explanationsIPR: '',
            explanationsFCIPT: '',
            knowledgeBefore: '',
            knowledgeAfter: '',
            technicalContents: '',
            easeOfUnderstanding: ''
        },
        best: '',
        worst: '',
        suggestions: '',
        comments: ''
    });

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === 'checkbox') {
            setFormData((prevData) => ({
                ...prevData,
                [name]: checked
                    ? [...prevData[name], value]
                    : prevData[name].filter((item) => item !== value)
            }));
        } else if (name in formData.ratings) {
            setFormData((prevData) => ({
                ...prevData,
                ratings: { ...prevData.ratings, [name]: value }
            }));
        } else {
            setFormData((prevData) => ({
                ...prevData,
                [name]: value
            }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        // Add your form submission logic here
    };

    return (
        <div className="container mx-auto p-8 bg-gray-100 min-h-screen">
            <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden">
                <div className="bg-blue-600 text-white py-4 px-6">
                    <h2 className="text-3xl font-bold text-center">FEEDBACK FORM</h2>
                </div>
                <form onSubmit={handleSubmit} className="p-6 space-y-6">
                    {/* Institution Details */}
                    <div className="space-y-4">
                        <div>
                            <label htmlFor="institutionName" className="block text-sm font-medium text-gray-700">
                                Name of the visiting Institution <span className="text-red-500">*</span>
                            </label>
                            <input type="text" id="institutionName" name="institutionName" value={formData.institutionName} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50" />
                        </div>
                        {/* Add similar styling for website and visitDate inputs */}
                    </div>

                    {/* Staff Details */}
                    <div className="space-y-4">
                        <div>
                            <label htmlFor="staffName" className="block text-sm font-medium text-gray-700">
                                Name of the in-charge staff member <span className="text-red-500">*</span>
                            </label>
                            <input type="text" id="staffName" name="staffName" value={formData.staffName} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50" />
                        </div>
                        {/* Add similar styling for staffEmail and staffMobile inputs */}
                    </div>

                    {/* Visit Details */}
                    <div className="space-y-4">
                        <div>
                            <label htmlFor="totalStudents" className="block text-sm font-medium text-gray-700">
                                Total number of students <span className="text-red-500">*</span>
                            </label>
                            <input type="number" id="totalStudents" name="totalStudents" value={formData.totalStudents} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50" />
                        </div>
                        {/* Add similar styling for accompanyingStaff input */}
                    </div>

                    {/* Sources and Campuses */}
                    <div className="space-y-4">
                        <fieldset>
                            <legend className="text-sm font-medium text-gray-700">How did you come to know about IPR? <span className="text-red-500">*</span></legend>
                            <div className="mt-2 space-y-2">
                                {['Internet', 'Social Media', 'Friends', 'Other'].map((source) => (
                                    <div key={source} className="flex items-center">
                                        <input type="checkbox" id={source.toLowerCase()} name="sources" value={source} checked={formData.sources.includes(source)} onChange={handleChange} className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded" />
                                        <label htmlFor={source.toLowerCase()} className="ml-2 block text-sm text-gray-700">{source}</label>
                                    </div>
                                ))}
                            </div>
                        </fieldset>
                        {/* Add similar styling for campuses checkboxes */}
                    </div>

                    {/* Ratings */}
                    <div className="space-y-4">
                        {['iprRating', 'fciptRating', 'knowledge', 'explanationsIPR', 'explanationsFCIPT', 'knowledgeBefore', 'knowledgeAfter', 'technicalContents', 'easeOfUnderstanding'].map((field, index) => (
                            <div key={index} className="rating-group">
                                <label className="block text-sm font-medium text-gray-700">How do you rate your experience? <span className="text-red-500">*</span></label>
                                <div className="mt-2 flex items-center space-x-4">
                                    {[1, 2, 3, 4, 5].map((value) => (
                                        <label key={value} className="flex items-center">
                                            <input
                                                type="radio"
                                                name={field}
                                                value={value}
                                                checked={formData.ratings[field] === value.toString()}
                                                onChange={handleChange}
                                                className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
                                            />
                                            <span className="ml-2 text-sm text-gray-700">{value}</span>
                                        </label>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Comments */}
                    <div className="space-y-4">
                        <div>
                            <label htmlFor="best" className="block text-sm font-medium text-gray-700">What did you like best about the visit?</label>
                            <textarea id="best" name="best" value={formData.best} onChange={handleChange} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50" rows="3"></textarea>
                        </div>
                        {/* Add similar styling for worst, suggestions, and comments textareas */}
                    </div>

                    <div>
                        <button type="submit" className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                            Submit Feedback
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default FeedbackForm;
