import React, { useState } from 'react';

const FeedbackForm = () => {
    const [formData, setFormData] = useState({
        institutionName: 'LDRP Institute of Technology and Research',
        website: '',
        visitDate: '',
        staffName: '',
        staffEmail: '',
        staffMobile: '',
        totalStudents: '',
        accompanyingStaff: '',
        sources: [],
        campuses: [],
        ratings: {
            iprRating: '',
            fciptRating: '',
            knowledge: '',
            explanationsIPR: '',
            explanationsFCIPT: '',
            knowledgeBefore: '',
            knowledgeAfter: '',
            technicalContents: '',
            easeOfUnderstanding: ''
        },
        best: '',
        worst: '',
        suggestions: '',
        comments: ''
    });

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === 'checkbox') {
            setFormData((prevData) => ({
                ...prevData,
                [name]: checked
                    ? [...prevData[name], value]
                    : prevData[name].filter((item) => item !== value)
            }));
        } else if (name in formData.ratings) {
            setFormData((prevData) => ({
                ...prevData,
                ratings: { ...prevData.ratings, [name]: value }
            }));
        } else {
            setFormData((prevData) => ({
                ...prevData,
                [name]: value
            }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        // Add your form submission logic here
    };

    return (
        <div className="bg-gradient-to-r from-blue-100 to-purple-100 min-h-screen py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-xl overflow-hidden">
                <div className="bg-gradient-to-r from-blue-500 to-purple-600 py-6 px-8">
                    <h2 className="text-3xl font-extrabold text-white text-center">FEEDBACK FORM</h2>
                </div>
                <form onSubmit={handleSubmit} className="p-8 space-y-8">
                    {/* Institution Details */}
                    <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                        <div>
                            <label htmlFor="institutionName" className="block text-sm font-medium text-gray-700">
                                Institution Name <span className="text-red-500">*</span>
                            </label>
                            <input type="text" id="institutionName" name="institutionName" value={formData.institutionName} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 transition duration-150 ease-in-out" />
                        </div>
                        <div>
                            <label htmlFor="website" className="block text-sm font-medium text-gray-700">
                                Website <span className="text-red-500">*</span>
                            </label>
                            <input type="text" id="website" name="website" value={formData.website} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 transition duration-150 ease-in-out" />
                        </div>
                    </div>

                    {/* Staff Details */}
                    <div className="grid grid-cols-1 gap-6 sm:grid-cols-3">
                        <div>
                            <label htmlFor="staffName" className="block text-sm font-medium text-gray-700">
                                Staff Name <span className="text-red-500">*</span>
                            </label>
                            <input type="text" id="staffName" name="staffName" value={formData.staffName} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 transition duration-150 ease-in-out" />
                        </div>
                        <div>
                            <label htmlFor="staffEmail" className="block text-sm font-medium text-gray-700">
                                Staff Email <span className="text-red-500">*</span>
                            </label>
                            <input type="email" id="staffEmail" name="staffEmail" value={formData.staffEmail} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 transition duration-150 ease-in-out" />
                        </div>
                        <div>
                            <label htmlFor="staffMobile" className="block text-sm font-medium text-gray-700">
                                Staff Mobile <span className="text-red-500">*</span>
                            </label>
                            <input type="tel" id="staffMobile" name="staffMobile" pattern="[0-9]{10}" value={formData.staffMobile} onChange={handleChange} required className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 transition duration-150 ease-in-out" />
                        </div>
                    </div>

                    {/* Sources and Campuses */}
                    <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                        <fieldset>
                            <legend className="text-base font-medium text-gray-700 mb-2">How did you learn about IPR? <span className="text-red-500">*</span></legend>
                            <div className="space-y-2">
                                {['Internet', 'Social Media', 'Friends', 'Other'].map((source) => (
                                    <div key={source} className="flex items-center">
                                        <input type="checkbox" id={source.toLowerCase()} name="sources" value={source} checked={formData.sources.includes(source)} onChange={handleChange} className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded" />
                                        <label htmlFor={source.toLowerCase()} className="ml-3 text-sm text-gray-700">{source}</label>
                                    </div>
                                ))}
                            </div>
                        </fieldset>
                        <fieldset>
                            <legend className="text-base font-medium text-gray-700 mb-2">IPR Campuses visited <span className="text-red-500">*</span></legend>
                            <div className="space-y-2">
                                {['IPR Main', 'FCIPT'].map((campus) => (
                                    <div key={campus} className="flex items-center">
                                        <input type="checkbox" id={campus.toLowerCase()} name="campuses" value={campus} checked={formData.campuses.includes(campus)} onChange={handleChange} className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded" />
                                        <label htmlFor={campus.toLowerCase()} className="ml-3 text-sm text-gray-700">{campus}</label>
                                    </div>
                                ))}
                            </div>
                        </fieldset>
                    </div>

                    {/* Ratings */}
                    <div className="space-y-6">
                        {['iprRating', 'fciptRating', 'knowledge', 'explanationsIPR', 'explanationsFCIPT', 'knowledgeBefore', 'knowledgeAfter', 'technicalContents', 'easeOfUnderstanding'].map((field, index) => (
                            <div key={index} className="rating-group">
                                <label className="block text-sm font-medium text-gray-700 mb-2">Rate your experience: {field} <span className="text-red-500">*</span></label>
                                <div className="flex items-center space-x-4">
                                    {[1, 2, 3, 4, 5].map((value) => (
                                        <label key={value} className="flex items-center">
                                            <input
                                                type="radio"
                                                name={field}
                                                value={value}
                                                checked={formData.ratings[field] === value.toString()}
                                                onChange={handleChange}
                                                className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300"
                                            />
                                            <span className="ml-2 text-sm text-gray-700">{value}</span>
                                        </label>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Comments */}
                    <div className="space-y-6">
                        {['best', 'worst', 'suggestions', 'comments'].map((field) => (
                            <div key={field}>
                                <label htmlFor={field} className="block text-sm font-medium text-gray-700 mb-2">
                                    {field.charAt(0).toUpperCase() + field.slice(1)}
                                </label>
                                <textarea
                                    id={field}
                                    name={field}
                                    value={formData[field]}
                                    onChange={handleChange}
                                    rows="3"
                                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 transition duration-150 ease-in-out"
                                ></textarea>
                            </div>
                        ))}
                    </div>

                    <div>
                        <button type="submit" className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 ease-in-out">
                            Submit Feedback
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default FeedbackForm;
