import React, { useState } from 'react';
import { motion } from 'framer-motion';


const FeedbackForm = () => {
    const [formData, setFormData] = useState({
        institutionName: '',
        website: '',
        visitDate: '',
        staffName: '',
        staffEmail: '',
        staffMobile: '',
        totalStudents: '',
        accompanyingStaff: '',
        sources: [],
        campuses: [],
        ratings: {
            iprRating: '',
            fciptRating: '',
            knowledge: '',
            explanationsIPR: '',
            explanationsFCIPT: '',
            knowledgeBefore: '',
            knowledgeAfter: '',
            technicalContents: '',
            easeOfUnderstanding: ''
        },
        best: '',
        worst: '',
        suggestions: '',
        comments: ''
    });

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === 'checkbox') {
            setFormData((prevData) => ({
                ...prevData,
                [name]: checked
                    ? [...prevData[name], value]
                    : prevData[name].filter((item) => item !== value)
            }));
        } else if (name in formData.ratings) {
            setFormData((prevData) => ({
                ...prevData,
                ratings: { ...prevData.ratings, [name]: value }
            }));
        } else {
            setFormData((prevData) => ({
                ...prevData,
                [name]: value
            }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        // Add your form submission logic here
    };

    return (
        <div className="container mx-auto p-5 bg-blue-50 min-h-screen">
            <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden">
                <div className="bg-blue-600 text-white py-4 px-6">
                    <h2 className="text-2xl font-bold text-center">Feedback Form</h2>
                </div>
                <form onSubmit={handleSubmit} className="p-6 space-y-6">
                    <div className="space-y-4">
                        {[
                            { label: "Name of the visiting Institution", name: "institutionName", type: "text" },
                            { label: "Website of the institution", name: "website", type: "text" },
                            { label: "Date of visit", name: "visitDate", type: "date" },
                            { label: "Name of the in-charge staff member", name: "staffName", type: "text" },
                            { label: "Email of in-charge staff member", name: "staffEmail", type: "email" },
                            { label: "Mobile number of in-charge staff member", name: "staffMobile", type: "tel" },
                            { label: "Total number of students", name: "totalStudents", type: "number" },
                            { label: "Number of accompanying staff members", name: "accompanyingStaff", type: "number" },
                        ].map((field, index) => (
                            <div key={index}>
                                <label htmlFor={field.name} className="block text-sm font-medium text-gray-700">
                                    {field.label} <span className="text-red-500">*</span>
                                </label>
                                <input
                                    type={field.type}
                                    id={field.name}
                                    name={field.name}
                                    value={formData[field.name]}
                                    onChange={handleChange}
                                    required
                                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                />
                            </div>
                        ))}
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            How did you come to know about IPR? <span className="text-red-500">*</span>
                        </label>
                        <div className="space-y-2">
                            {["Internet", "Social Media", "Friends", "Other"].map((source) => (
                                <div key={source} className="flex items-center">
                                    <input
                                        type="checkbox"
                                        id={source.toLowerCase()}
                                        name="sources"
                                        value={source}
                                        checked={formData.sources.includes(source)}
                                        onChange={handleChange}
                                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                    />
                                    <label htmlFor={source.toLowerCase()} className="ml-2 block text-sm text-gray-700">
                                        {source === "Other" ? source : `From ${source.toLowerCase()}`}
                                    </label>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            IPR Campuses visited during the trip? <span className="text-red-500">*</span>
                        </label>
                        <div className="space-y-2">
                            {["IPR Main", "FCIPT"].map((campus) => (
                                <div key={campus} className="flex items-center">
                                    <input
                                        type="checkbox"
                                        id={campus.toLowerCase().replace(' ', '')}
                                        name="campuses"
                                        value={campus}
                                        checked={formData.campuses.includes(campus)}
                                        onChange={handleChange}
                                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                    />
                                    <label htmlFor={campus.toLowerCase().replace(' ', '')} className="ml-2 block text-sm text-gray-700">
                                        {campus}
                                    </label>
                                </div>
                            ))}
                        </div>
                    </div>

                    {['iprRating', 'fciptRating', 'knowledge', 'explanationsIPR', 'explanationsFCIPT', 'knowledgeBefore', 'knowledgeAfter', 'technicalContents', 'easeOfUnderstanding'].map((field, index) => (
                        <div key={index} className="space-y-2">
                            <label className="block text-sm font-medium text-gray-700">
                                How do you rate your {field.replace(/([A-Z])/g, ' $1').toLowerCase()}? <span className="text-red-500">*</span>
                            </label>
                            <div className="flex space-x-4">
                                {[1, 2, 3, 4, 5].map((value) => (
                                    <label key={value} className="flex items-center">
                                        <input
                                            type="radio"
                                            name={field}
                                            value={value}
                                            checked={formData.ratings[field] === value.toString()}
                                            onChange={handleChange}
                                            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
                                        />
                                        <span className="ml-2 text-sm text-gray-700">{value}</span>
                                    </label>
                                ))}
                            </div>
                        </div>
                    ))}

                    {[
                        { label: "What did you like best about the visit?", name: "best" },
                        { label: "What did you like the least about the visit?", name: "worst" },
                        { label: "Any suggestions for improvement?", name: "suggestions" },
                        { label: "Any additional comments?", name: "comments" },
                    ].map((field, index) => (
                        <div key={index}>
                            <label htmlFor={field.name} className="block text-sm font-medium text-gray-700">
                                {field.label}
                            </label>
                            <textarea
                                id={field.name}
                                name={field.name}
                                value={formData[field.name]}
                                onChange={handleChange}
                                rows={3}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            />
                        </div>
                    ))}

                    <div>
                        <button
                            type="submit"
                            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                        >
                            Submit Feedback
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default FeedbackForm;
