require('dotenv').config(); // Load environment variables from .env file

const nodemailer = require('nodemailer');

const transporter = nodemailer.createTransport({
    host: process.env.SMTP_HOST, // Use SMTP host from .env
    port: process.env.SMTP_PORT, // Use SMTP port from .env
    secure: false, // Set to true if using port 465
    auth: {
        user: process.env.EMAIL_USER, // Use email from .env
        pass: process.env.EMAIL_PASS, // Use password from .env
    },
});

// Function to send email
const sendEmail = (to, subject, text) => {
    const mailOptions = {
        from: 'thakkarkrutika066@gmail.com',
        to: to,
        subject: subject,
        text: text,
    };

    return transporter.sendMail(mailOptions);
};

module.exports = { sendEmail };
