const competitionEndpoints = {
    "Student's Model-1": '/api/student-model-1',
    "Student's Model-2": '/api/student-model-2',
    "Quiz": '/api/quiz',
    "Eloquence - English": '/api/eloquence/english',
    "Eloquence - Hindi": '/api/eloquence/hindi',
    "Eloquence - Gujarati": '/api/eloquence/gujarati',
    "Essay - English": '/api/essay/english',
    "Essay - Hindi": '/api/essay/hindi',
    "Essay - Gujarati": '/api/essay/gujarati',
    "Poster": '/api/poster',
    "Skit": '/api/skit',
    "Teacher Model": '/api/teacher-model',
};

const IPRPage2 = () => {
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(0);
    const [schoolId, setSchoolId] = useState(null);
    const [accompanyingTeacherId, setAccompanyingTeacherId] = useState(null);
    const [selectedCompetitions, setSelectedCompetitions] = useState([]);
    const [currentCompetition, setCurrentCompetition] = useState(null);
    const [competitionDetails, setCompetitionDetails] = useState({});
    const [competition, setCompetition] = useState('');
    const [filledCompetitions, setFilledCompetitions] = useState([]);
    const [filledFields, setFilledFields] = useState(0);
    const [formData, setFormData] = useState({
        // School form fields
        step0_field0: '', // coordinatorTeacherName
        step0_field1: '', // coordinatorTeacherMobile
        step0_field2: '', // schoolName
        step0_field3: '', // address
        step0_field4: '', // city
        step0_field5: '', // pincode
        step0_field6: '', // affiliationNumber
        
        // Accompanying teacher form fields
        step1_field0: '', // teacherName
        step1_field1: '', // gender
        step1_field2: '', // requiresAccommodation
    });


    const currentFields = steps[currentStep].fields;
    const totalFields = currentFields.length;


    const handleInputChange = (index, value, type = 'text') => {
        const key = `step${currentStep}_field${index}`;

        let newValue;

        if (type === 'checkbox') {
            newValue = value.target.checked;
        } else if (type === 'file') {
            const file = value.target.files[0];
            const fileExtension = file?.name.split('.').pop().toLowerCase();

            const videoExtensions = ['mp4', 'avi', 'mkv', 'mov', 'wmv'];
            if (file && videoExtensions.includes(fileExtension)) {
                if (file.size > 10 * 1024 * 1024) {
                    alert('Video file size exceeds the 10MB limit.');
                    return;
                }
                newValue = { type: 'video', file };
            } else if (file && fileExtension === 'pdf') {
                newValue = { type: 'pdf', file };
            } else {
                alert('Invalid file format. Please upload a valid video or PDF file.');
                return;
            }
        } else {
            newValue = value;
        }

        setFormData({ ...formData, [key]: newValue });

        const stepFieldKeys = currentFields.map((_, i) => `step${currentStep}_field${i}`);
        const filledFieldsInStep = stepFieldKeys.filter(key => formData[key] && formData[key].toString().trim() !== "").length;

        const newFilledFields = filledFieldsInStep + (newValue.toString().trim() !== "" && !formData[key] ? 1 : 0);
        setFilledFields(newFilledFields);
    };

    const handleSchoolSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:3000/api/school', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    coordinatorName: formData.step0_field0,
                    coordinatorMobile: formData.step0_field1,
                    schoolName: formData.step0_field2,
                    schoolAddress: formData.step0_field3,
                    schoolCity: formData.step0_field4,
                    schoolPincode: formData.step0_field5,
                    schoolAffiliationNumber: formData.step0_field6
                }),
            });
            if (!response.ok) throw new Error('Failed to submit school details');
            const result = await response.json();
            setSchoolId(result.schoolId);
            setCurrentStep(1);
        } catch (error) {
            console.error('Error submitting school details:', error);
            alert('Failed to submit school details. Please try again.');
        }
    };

    const handleAccompanyingTeacherSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:3000/api/accompanyingTeacher', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    schoolId: schoolId,
                    name: formData.step1_field0,
                    gender: formData.step1_field1,
                    requiresAccommodation: formData.step1_field2
                }),
            });
            if (!response.ok) throw new Error('Failed to submit accompanying teacher details');
            const result = await response.json();
            setAccompanyingTeacherId(result.teacherId);
            setCurrentStep(2);
        } catch (error) {
            console.error('Error submitting accompanying teacher details:', error);
            alert('Failed to submit accompanying teacher details. Please try again.');
        }
    };

    const handleCompetitionSelection = async (competitionName) => {
        if (!selectedCompetitions.includes(competitionName)) {
            try {
                const response = await fetch('http://localhost:3000/api/createCompetition', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        schoolId: schoolId,
                        competitionName: competitionName
                    }),
                });
                if (!response.ok) throw new Error('Failed to create competition');
                setSelectedCompetitions([...selectedCompetitions, competitionName]);
            } catch (error) {
                console.error('Error creating competition:', error);
                alert('Failed to select competition. Please try again.');
            }
        }
        setCurrentCompetition(competitionName);
    };

    const handleCompetitionDetailSubmit = async () => {
        const endpoint = competitionEndpoints[currentCompetition];
        try {
            const response = await fetch(endpoint, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    schoolId: schoolId,
                    ...competitionDetails[currentCompetition]
                }),
            });
            if (!response.ok) throw new Error(`Failed to submit ${currentCompetition} details`);
            setCurrentCompetition(null);
        } catch (error) {
            console.error(`Error submitting ${currentCompetition} details:`, error);
            alert(`Failed to submit ${currentCompetition} details. Please try again.`);
        }
    };

    const handleCompetitionChange = (event) => {
        const selectedCompetition = event.target.value;
        setCompetition(selectedCompetition);
        handleInputChange(0, selectedCompetition);
    };

    const handleCompetitionDetailChange = (index, value) => {
        const competitionKey = competition + `_detail${index}`;
        setCompetitionDetails({ ...competitionDetails, [competitionKey]: value });
    };

    const handleSaveAndContinue = async (event) => {
        event.preventDefault();

        const currentCompetitionKey = competition;

        if (competitionFilled.has(currentCompetitionKey)) {
            alert("You have already filled details for this competition.");
            return;
        }

        const formData = {};
        Object.keys(competitionDetails).forEach((key) => {
            if (key.startsWith(currentCompetitionKey)) {
                formData[key.replace(`${currentCompetitionKey}_`, '')] = competitionDetails[key];
            }
        });

        try {
            const endpoint = competitionEndpoints[currentCompetitionKey];
            if (!endpoint) {
                throw new Error('Invalid competition');
            }

            const response = await fetch(endpoint, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    ...formData,
                    schoolId: schoolId,
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to save competition details');
            }

            const result = await response.json();
            console.log('Save result:', result);

            setCompetitionFilled(new Set([...competitionFilled, currentCompetitionKey]));
            setCompetition('');
            setCompetitionDetails({});
            setCurrentStep(2);

            alert('Competition details saved successfully!');
        } catch (error) {
            console.error('Error saving competition details:', error);
            alert('Failed to save competition details. Please try again.');
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (competitionFilled.size === 0) {
            alert("Please fill and save at least one competition before submitting.");
            return;
        }

        try {
            const submissionPromises = Array.from(competitionFilled).map(async (competitionName) => {
                const response = await fetch('localhost:3000/api/createCompetition', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        title: competitionName,
                        schoolId: schoolId,
                    }),
                });

                if (!response.ok) {
                    throw new Error(`Failed to create competition entry for ${competitionName}`);
                }

                return response.json();
            });

            await Promise.all(submissionPromises);

            alert('All competitions submitted successfully!');
            // Reset the form
            setCurrentStep(1);
            setCompetition('');
            setCompetitionFilled(new Set());
            setCompetitionDetails({});
            setFormData({});
        } catch (error) {
            console.error('Error submitting competitions:', error);
            alert('Failed to submit competitions. Please try again.');
        }
    };

    const handlePrevious = () => {
        if (currentStep > 0) {
            setCurrentStep(currentStep - 1);
            if (currentStep === 2) {
                setCompetition("");
                setCompetitionDetails({});
            }
        }
    };
    const handleFinalSubmit = async () => {
        if (selectedCompetitions.length === 0) {
            alert("Please select and fill details for at least one competition before submitting.");
            return;
        }
    
        try {
            // Submit final form data
            const response = await fetch('http://localhost:3000/api/submitForm', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    schoolId: schoolId,
                    accompanyingTeacherId: accompanyingTeacherId,
                    competitions: selectedCompetitions
                }),
            });
    
            if (!response.ok) {
                throw new Error('Failed to submit form');
            }
    
            // Form submitted successfully
            alert('Form submitted successfully!');
    
            // Reset the form
            setCurrentStep(0);
            setFormData({});
            setSchoolId(null);
            setAccompanyingTeacherId(null);
            setSelectedCompetitions([]);
            setCurrentCompetition(null);
            setCompetitionDetails({});
    
            // Navigate to a thank you page
            navigate('/thank-you');
        } catch (error) {
            console.error('Error submitting form:', error);
            alert('Failed to submit form. Please try again.');
        }
    };
    

    return (
        <div className="bg-gray-100 p-12 flex justify-center items-center min-h-screen">
            <div className="bg-white p-8 border border-blue-500 rounded-lg flex flex-col justify-between min-h-screen shadow-lg w-full z-0">
                <h1 className="text-3xl font-bold text-gray-800 text-center mb-4">
                    Application form - National Science Day-NSD2024
                </h1>

                <div className="relative flex justify-between items-center mt-4 mb-6">
                    {steps.map((_, index) => (
                        <React.Fragment key={index}>
                            <div className="relative z-10">
                                <div
                                    className={`w-12 h-12 flex items-center justify-center rounded-full border-2 ${index <= currentStep
                                            ? 'border-blue-600 bg-blue-600 text-white'
                                            : 'border-gray-300 bg-white text-gray-600'
                                        } font-bold`}
                                >
                                    {index + 1}
                                </div>
                            </div>
                            {index < steps.length - 1 && (
                                <div className="relative rounded-full w-full h-0.5 mx-2 bg-gray-300">
                                    <div
                                        className="h-full bg-blue-600"
                                        style={{
                                            width: index < currentStep
                                                ? '100%'
                                                : index === currentStep
                                                    ? `${Math.min((filledFields / totalFields) * 100, 100)}%`
                                                    : '0%',
                                            borderRadius: '999px',
                                            transition: 'width 0.3s ease',
                                        }}
                                    ></div>
                                </div>
                            )}
                        </React.Fragment>
                    ))}
                </div>

                <div className="bg-blue-600 text-white p-4 rounded-md font-semibold text-lg mb-4">
                    {steps[currentStep].title}
                </div>

                <form onSubmit={handleSubmit} className="flex-grow">
                    {currentFields.map((field, index) => (
                        <div key={index} className="mb-6">
                            <label className="block mb-1 font-semibold">
                                {field.label}
                                {field.required && (
                                    <span className="text-red-600 font-bold"> *</span>
                                )}
                            </label>
                            {field.type === 'select' ? (
                                <div className="relative">
                                    <select
                                        value={formData[`step${currentStep}_field${index}`] || ''}
                                        onChange={(field.label.includes("Select a competition") ? handleCompetitionChange : (e) => handleInputChange(index, e.target.value))}
                                        required={field.required}
                                        className="block w-full p-2 border border-gray-300 rounded bg-white pr-8 appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent z-10"
                                    >
                                        {field.options.map((option, idx) => (
                                            <option key={idx} value={option.value}>
                                                {option.label}
                                            </option>
                                        ))}
                                    </select>
                                    <div className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                                        <svg
                                            xmlns="http://www.w3.org/2000/svg"
                                            className="h-6 w-auto text-gray-600"
                                            viewBox="0 0 20 20"
                                            fill="currentColor"
                                            aria-hidden="true"
                                        >
                                            <path
                                                fillRule="evenodd"
                                                d="M5.23 7.21a.75.75 0 011.06 0L10 10.5l3.71-3.29a.75.75 0 111.04 1.08l-4 3.5a.75.75 0 01-1.04 0l-4-3.5a.75.75 0 010-1.08z"
                                                clipRule="evenodd"
                                            />
                                        </svg>
                                    </div>
                                </div>
                            ) : (
                                <input
                                    type={field.type}
                                    value={formData[`step${currentStep}_field${index}`] || ''}
                                    onChange={(e) => handleInputChange(index, e.target.value)}
                                    required={field.required}
                                    className="block w-full p-2 border border-gray-300 rounded bg-white pr-8 appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent z-10"
                                />
                            )}
                            <p className="text-sm text-gray-500">{field.helperText}</p>
                        </div>
                    ))}

                    {competition && (
                        <div className="mt-4 border-t border-gray-400 pt-4">
                            <h3 className="text-lg font-semibold text-gray-700">{competition}</h3>
                            {competitionFieldsConfig[competition].note1 && (
                                <div className="note bg-gray-100 p-4 rounded-lg border-l-4 border-red-600 mb-4">
                                    <h3 className="text-lg text-red-600 mb-2">NOTE:</h3>
                                    <strong className="text-gray-800"></strong>
                                    <ul className="text-gray-800 list-disc ml-4">
                                        <li>{competitionFieldsConfig[competition].note1}</li>
                                        {competitionFieldsConfig[competition].note2 && (
                                            <li>{competitionFieldsConfig[competition].note2}</li>
                                        )}
                                        {competitionFieldsConfig[competition].note2 && (
                                            <li>
                                                {competitionFieldsConfig[competition].note3}{' '}
                                                <a href={competitionFieldsConfig[competition].note4} className="font-medium text-blue-600 dark:text-blue-500 hover:underline">
                                                    {competitionFieldsConfig[competition]?.note4}
                                                </a>
                                            </li>
                                        )}
                                    </ul>
                                </div>
                            )}

                            {competitionFieldsConfig[competition].fields.map((field, index) => (
                                <div key={index} className="mb-6">
                                    <label className="block mb-1 text-md font-semibold">
                                        {field.label}
                                        {field.required && <span className="text-red-600 font-bold"> *</span>}
                                    </label>
                                    {field.type === 'combined' ? (
                                        <div className="space-y-4">
                                            <input
                                                type="file"
                                                accept=".mp4,.avi,.mkv,.mov,.wmv"
                                                onChange={(e) => handleInputChange(index, e, 'file')}
                                                required={field.required}
                                                className="block w-full p-2 border border-gray-300 rounded bg-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                            />
                                            <input
                                                type="text"
                                                value={competitionDetails[`${competition}_detail${index}`] || ''}
                                                onChange={(e) => handleCompetitionDetailChange(index, e.target.value)}
                                                required={field.required}
                                                className="block w-full p-2 border border-gray-300 rounded bg-white pr-8 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent z-10"
                                                placeholder="Enter your YouTube video link"
                                            />
                                        </div>
                                    ) : field.type === 'select' ? (
                                        <div className="relative">
                                            <select
                                                value={competitionDetails[`${competition}_detail${index}`] || ''}
                                                onChange={(e) => handleCompetitionDetailChange(index, e.target.value)}
                                                required={field.// ... (continuing from the previous code)

                                                    required} className="block w-full p-2 border border-gray-300 rounded bg-white pr-8 appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent z-10"
                                            >
                                                {field.options.map((option, idx) => (
                                                    <option key={idx} value={option.value}>
                                                        {option.label}
                                                    </option>
                                                ))}
                                            </select>
                                            <div className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                                                <svg
                                                    xmlns="http://www.w3.org/2000/svg"
                                                    className="h-6 w-auto text-gray-600"
                                                    viewBox="0 0 20 20"
                                                    fill="currentColor"
                                                    aria-hidden="true"
                                                >
                                                    <path
                                                        fillRule="evenodd"
                                                        d="M5.23 7.21a.75.75 0 011.06 0L10 10.5l3.71-3.29a.75.75 0 111.04 1.08l-4 3.5a.75.75 0 01-1.04 0l-4-3.5a.75.75 0 010-1.08z"
                                                        clipRule="evenodd"
                                                    />
                                                </svg>
                                            </div>
                                        </div>
                                    ) : (
                                        <CompetitionFields
                                            field={field}
                                            competition={competition}
                                            index={index}
                                            competitionDetails={competitionDetails}
                                            handleCompetitionDetailChange={handleCompetitionDetailChange}
                                            handleInputChange={handleInputChange}
                                        />
                                    )}
                                    <p className="text-sm text-gray-500">{field.helperText}</p>
                                </div>
                            ))}
                        </div>
                    )}
                </form>

                <div className="flex justify-between mt-auto">
                    <button
                        type="button"
                        className="px-4 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400"
                        onClick={handlePrevious}
                        disabled={currentStep === 0}
                    >
                        Back
                    </button>
                    {currentStep === 2 ? (
                        <>
                            <button
                                type="button"
                                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                                onClick={handleSaveAndContinue}
                            >
                                Save & Continue
                            </button>
                            <button
                                type="submit"
                                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                                onClick={handleSubmit}
                            >
                                Submit
                            </button>
                        </>
                    ) : (
                        <button
                            type="submit"
                            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                            onClick={handleSubmit}
                        >
                            Next
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default IPRPage2;
